void matchFBRecordsToInvoice()
{
    TMSFreightBillDetailUnMatched   rec;
    TMSFreightBillDetail            freightBillDetail;

    for (rec = tmsFreightBillDetailUnMatched_DS.getFirst(true) ? tmsFreightBillDetailUnMatched_DS.getFirst(true) : tmsFreightBillDetailUnMatched_DS.cursor();
    rec;
    rec = tmsFreightBillDetailUnMatched_DS.getNext())
    {
        freightBillDetail = TMSFreightBillDetail::find(rec.RecId);
        freightBillDetail.matchRecord(tmsInvoiceLine);
    }
}

void matchInvoiceRecordsToFB()
{
    TMSInvoiceLine              rec;
    TMSFreightBillDetail        freightBillDetail;

    for (rec = tmsInvoiceLine_DS.getFirst(true) ? tmsInvoiceLine_DS.getFirst(true) : tmsInvoiceLine_DS.cursor();
    rec;
    rec = tmsInvoiceLine_DS.getNext())
    {
        freightBillDetail = TMSFreightBillDetail::find(tmsFreightBillDetailUnmatched.RecId);
        freightBillDetail.matchRecord(rec);
    }
}

