public void executeQuery()
{
    str                     filter;

    filter = element.invoiceFilter(tmsInvoiceLine_q.dataSourceTable(tableNum(TMSInvoiceLine)));
    qbrInvoiceFilter.value(filter);

    // Freight Bill Type Filter
    qbrInvoiceFreightType.value(freightTypeFilter);

    // Header Status
    if (!showSubApp.checked())
    {
        qbrInvoiceStatus.value(strFmt('((%1 == %2) || (%1 == %3) || (%1 == %4))',
                                      fieldStr(TMSInvoiceLine, InvoiceStatus),
                                      enum2int(TMSInvoiceStatus::Open),
                                      enum2int(TMSInvoiceStatus::PartiallyApproved),
                                      enum2int(TMSInvoiceStatus::Rejected)));
    }

    else
    {
        qbrInvoiceStatus.value('');
    }

    super();
}

public void init()
{
    super();

    qbrInvoiceFilter        = tmsInvoiceLine_q.dataSourceTable(tableNum(TMSInvoiceLine)).addRange(fieldNum(TMSInvoiceLine, DataAreaId));
    qbrInvoiceFreightType   = tmsInvoiceLine_q.dataSourceName(this.name()).addRange(fieldNum(TMSInvoiceLine, FreightBillTypeId));
    qbrInvoiceStatus        = tmsInvoiceLine_q.dataSourceName(this.name()).addRange(fieldNum(TMSInvoiceLine, InvoiceStatus));

    this.cacheAddMethod(tableMethodStr(TMSInvoiceLine, displayAmountRemaining));
    this.cacheAddMethod(tableMethodStr(TMSInvoiceLine, displayTotalReconciled));
    this.cacheAddMethod(tableMethodStr(TMSInvoiceLine, displayAmountMatched));
}

public int active()
{
    int                  ret;
    TMSInvoiceStatus     status;

    ret = super();

    matchButton.enabled(tmsInvoiceLine.RecId != 0 && TMSFreightBillDetailUnMatched.RecId != 0);

    tmsFreightBillDetailUnmatched_ds.research();

    status = TMSInvoiceTable::find(tmsInvoiceLine.InternalInvNumber).InvoiceStatus;

    submitApproval.enabled(status == TMSInvoiceStatus::Open                 ||
                           status == TMSInvoiceStatus::PartiallyApproved    ||
                           status == TMSInvoiceStatus::Rejected);

    return ret;
}

