    super();

    qbrUnMatchedFilter = tmsFreightBillDetailUnMatched_q.dataSourceTable(tableNum(TMSFreightBillDetailUnMatched)).addRange(fieldNum(TMSFreightBillDetailUnMatched, DataAreaId));
    qbrUnMatchedFreightType     = tmsFreightBillDetailUnmatched_q.dataSourceTable(tableNum(TMSFreightBillDetailUnMatched)).addRange(fieldNum(TMSFreightBillDetailUnMatched, FreightBillTypeId));

    qbrUnMatchedFreightStatus   = tmsFreightBillDetailUnmatched_q.dataSourceTable(tableNum(TMSFreightBillDetailUnMatched)).addRange(fieldNum(TMSFreightBillDetailUnMatched, FreightBillMatchStatus));
    qbrUnMatchedFreightStatus.value(SysQuery::valueNot(TMSFreightBillMatchStatus::FullyMatched));

    this.cacheAddMethod(tableMethodStr(TMSFreightBillDetailUnMatched, displayAmountMatched));
    this.cacheAddMethod(tableMethodStr(TMSFreightBillDetailUnMatched, displayAmountRemaining));
}

display TMSCount imageIdx(TMSFreightBillDetailUnMatched _freightBill)
{
    int     ret;
    TMSFreightBillDetailUnMatched   matched;
    TMSFBInvMatch                   matchedRecords;

    matched = _freightBill;

    select firstonly RecId from matchedRecords
        where matchedRecords.FreightBillDetailRecId  == matched.RecId &&
              matchedRecords.InvoiceLineRecId        == tmsInvoiceLine.RecId;

    if (matchedRecords.RecId != 0)
    {
        ret = 0;
    }
    else
    {
        ret = -1;
    }

    return ret;
}

