void clicked()
{
    TMSInvoiceTable                     invoiceTable    = TMSInvoiceTable::find(tmsInvoiceLine.InternalInvNumber, true);
    Args                                args;
    WorkflowVersionTable                configTable;
    WorkflowWorkItemTable               workflowWorkItem;

    super();

    // Workflow
    configTable = Workflow::findWorkflowConfigToActivateForType(workFlowTypeStr(TMSInvoiceApproval), invoiceTable.RecId, invoiceTable.TableId);

    if (configTable.RecId)
    {
        args = new Args();
        args.record(invoiceTable);

        if (invoiceTable.InvoiceStatus == TMSInvoiceStatus::Open ||
            invoiceTable.InvoiceStatus == TMSInvoiceStatus::PartiallyApproved)
        {
            args.menuItemName(menuitemActionStr(TMSInvoiceApprovalSubmit));
            TMSInvoiceApprovalSubmitManager::mainConfigTable(args, configTable);
        }

        else
        {
            select firstonly workflowWorkItem
                where workflowWorkItem.RefRecId     == invoiceTable.RecId   &&
                      workflowWorkItem.RefTableId   == invoiceTable.TableId &&
                      workflowWorkItem.CompanyId    == curext()             &&
                      workflowWorkItem.Status       == WorkflowWorkItemStatus::Pending;

            TMSInvoiceApprovalResubmitActionMgr::main(args, workflowWorkItem);
        }
    }

    // Auto-Submission
    else
    {
        TMSInvoiceApproval::submitInvoice(invoiceTable, true, true, true);
    }

    tmsInvoiceLine_ds.executeQuery();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    tmsInvoiceLine_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    freightTypeFilter = this.valueStr();

    tmsInvoiceLine_ds.executeQuery();
    tmsFreightBillDetailUnmatched_ds.executeQuery();

    return ret;
}

void clicked()
{
    super();

    tmsFreightBillDetail.unmatchRecord(tmsInvoiceLine);

    tmsInvoiceLine_ds.reread();
    tmsInvoiceLine_ds.refresh();
    tmsfbInvMatch_ds.research();
    tmsFreightBillDetailUnMatched_ds.research(true);
}

void clicked()
{
    super();

    if (tmsFreightBillDetailUnMatched_DS.getFirst(true))
    {
        element.matchFBRecordsToInvoice();
    }
    else
    {
        element.matchInvoiceRecordsToFB();
    }

    tmsInvoiceLine_ds.reread();
    tmsInvoiceLine_ds.refresh();
    tmsfbInvMatch_ds.research(true);
    tmsFreightBillDetailUnMatched_ds.research();
}

void clicked()
{
    Args                 args = new Args();
    TMSFreightBillDetail fbDetail = TMSFreightBillDetail::find(tmsFreightBillDetailUnmatched.FreightBillRecId, true);

    args.caller(this);
    args.record(fbDetail);

    new MenuFunction(menuitemDisplayStr(TMSFBDetailReconcile), MenuItemType::Display).run(args);
}

void clicked()
{
    Args            args;
    FormRun         formRun;

    args = new Args(formStr(TMSFBDetailReconcile));
    args.caller(this);
    args.record(tmsFreightBillDetailUnmatched);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
    formRun.detach();

    tmsFreightBillDetailUnmatched_ds.research();
}
