[Form]
public class TMSInvoiceTable extends FormRun
{
    int                     counter;
    TMSFreightBillTypeId    fbTypeFilter;
    str                     invoiceRecIds;
    RecId                   savedLine;
    TMSInvoiceTable         filteredInvoiceTable;

}

private void refreshInvoiceTableRecord()
{
    tmsInvoiceTable_ds.reread();
    tmsInvoiceTable_ds.refresh();
}

public void init()
{
    super();

    if (element.args() && element.args().record() && element.args().record().TableId == tableNum(TMSInvoiceTable))
    {
        filteredInvoiceTable = element.args().record();
    }

    if (workflowTable::findActiveTemplate(workFlowTypeStr(TMSInvoiceApproval)))
    {
        submitApproval.visible(false);
    }

    else if (counter == 0)
    {
        this.design().workflowEnabled(false);
        this.design().workflowDatasource('');
        assumeControl.visible(false);
        ++counter;
        this.init(); // Must run init again to initialize new form design
    }
}

void toggleInvoiceFields(TMSInvoiceStatus _status)
{
    DictTable   dictTable   = new DictTable(tableNum(TMSInvoiceTable));
    DictField   dictField;
    Set         noEdit      = new Set(Types::String);
    int         i;
    int         totalFields;
    Set         set         = new Set(Types::String);

    // Set of fields to stay editable
    if (_status == TMSInvoiceStatus::Submitted ||
        _status == TMSInvoiceStatus::Resubmitted)
    {
        set.add('VendInvoiceId');
    }

    noEdit.add('TMSInvoiceAmount');

    noEdit.add('InvoiceAmount');
    noEdit.add('CurrencyCode');

    totalFields = dictTable.fieldCnt();

    for (i = 0; i < totalFields; ++i)
    {
        dictField = new DictField(tableNum(TMSInvoiceTable), dictTable.fieldCnt2Id(i));

        // All fields allow edit if open or rejected
        if (!dictField.isSystem() && !noEdit.in(dictField.name()) && (_status == TMSInvoiceStatus::Open || _status == TMSInvoiceStatus::Rejected))
        {
            tmsInvoiceTable_ds.object(dictField.id()).allowEdit(true);
        }

        else if (!dictField.isSystem() && !set.in(dictField.name()))
        {
            tmsInvoiceTable_ds.object(dictField.id()).allowEdit(false);
        }
    }
}

void toggleObjects()
{
    submitApproval.enabled(tmsInvoiceTable.InvoiceStatus == TMSInvoiceStatus::Open       ||
                           tmsInvoiceTable.InvoiceStatus == TMSInvoiceStatus::Rejected   ||
                           tmsInvoiceTable.InvoiceStatus == TMSInvoiceStatus::PartiallyApproved);

    element.toggleInvoiceFields(tmsInvoiceTable.InvoiceStatus);

    tmsInvoiceJournal.enabled(TMSFreightBillDetail::existsInvoiceJournal(tmsInvoiceTable.RouteCode,
                                                                         tmsInvoiceTable.LoadId,
                                                                         tmsInvoiceTable.InternalInvNumber));

    assumeControl.enabled(this.workflowUserIsDefault() && tmsInvoiceTable.InvoiceStatus == TMSInvoiceStatus::Rejected);
}

boolean workflowUserIsDefault()
{
    WorkflowWorkItemTable workflowWorkItem;

    select firstonly forupdate workflowWorkItem
        where workflowWorkItem.RefRecId     == tmsInvoiceTable.RecId    &&
              workflowWorkItem.RefTableId   == tmsInvoiceTable.TableId  &&
              workflowWorkItem.CompanyId    == curext()                 &&
              workflowWorkItem.Status       == WorkflowWorkItemStatus::Pending;

    return workflowWorkItem.UserId == TMSParameters::find().WorkflowUser;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(this.name());
    qbds.addRange(fieldNum(TMSInvoiceTable, DataAreaId)).value(strFmt('((%1 != %2))',
                                                                      fieldStr(TMSInvoiceTable, InvoiceStatus),
                                                                      enum2int(TMSInvoiceStatus::Approved)));

    // for Freight Bill Type filter
    qbds.addRange(fieldNum(TMSInvoiceTable, RecId));

    this.cacheAddMethod(tableMethodStr(TMSInvoiceTable, displayNotReconciled));
    this.cacheAddMethod(tableMethodStr(TMSInvoiceTable, displayTotalMatched));
    this.cacheAddMethod(tableMethodStr(TMSInvoiceTable, displayReconciled));
}

public int active()
{
    int ret;

    ret = super();

    element.toggleObjects();

    return ret;
}

public void initValue()
{
    super();

    tmsInvoiceTable.setInternalInvNum();
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceName(this.name());

    if (filteredInvoiceTable)
    {
        qbds.addRange(fieldNum(TMSInvoiceTable, RecId)).value(queryValue(filteredInvoiceTable.RecId));
    }

    super();
}

public void write()
{
    super();

    savedLine = tmSinvoiceLine.RecId;

    element.refreshInvoiceTableRecord();
}

public void executeQuery()
{
    super();

    if (savedLine)
    {
        tmsInvoiceLine_ds.positionToRecord(TMSInvoiceLine::findByRecId(savedLine));
    }

    savedLine = 0;
}

public int active()
{
    int ret;

    ret = super();

    tmsInvoiceLine_ds.allowEdit(tmsInvoiceLine.InvoiceLineStatus    == TMSInvoiceLineStatus::Open        ||
                               (tmsInvoiceLine.InvoiceLineStatus    == TMSInvoiceLineStatus::Rejected    &&
                                tmsInvoiceTable.InvoiceStatus      != TMSInvoiceStatus::Submitted        &&
                                tmsInvoiceTable.InvoiceStatus      != TMSInvoiceStatus::Approved));

    tmsInvoiceLineReconcile.enabled(tmsInvoiceLine.amountMatched() < tmsInvoiceLine.NetAmount);
    tmsRejectInvoiceLine.enabled(tmsInvoiceLine.InvoiceLineStatus == TMSInvoiceLineStatus::Open);

    return ret;
}

public void initValue()
{
    super();

    tmsInvoiceLine.RefRecId             = tmsInvoiceTable.RecId;
    tmsInvoiceLine.InternalInvNumber    = tmsInvoiceTable.InternalInvNumber;
    tmsInvoiceLine.Qty                  = 1;
    tmsInvoiceLine.initFromInvoiceTable();
}

public void delete()
{
    super();

    // Check if no other records are not marked to avoid rereading the header record multiple times when a user deletes multiple child records.
    if (this.recordsMarked().lastIndex() == 0)
    {
        element.refreshInvoiceTableRecord();
    }
}

void clicked()
{
    super();

    TMSInvoiceApproval::submitInvoice(tmsInvoiceTable, true, true);

    tmsInvoiceTable_ds.research(true);
    tmsInvoiceLine_ds.research(true);
}

void clicked()
{
    TMSInvoiceApproval::assumeWorkflowControl(tmsInvoiceTable);

    tmsInvoiceTable_ds.research(true);
}

public boolean modified()
{
    boolean              ret;
    QueryBuildDataSource qbds;

    ret = super();

    qbds = tmsInvoiceTable_q.dataSourceTable(tableNum(TMSInvoiceTable));

    if (hideApproved.checked())
    {
        qbds.range(1).value(strFmt('((%1 != %2))',
                                   fieldStr(TMSInvoiceTable, InvoiceStatus),
                                   enum2int(TMSInvoiceStatus::Approved)));
    }

    else
    {
        qbds.range(1).value('');
    }

    tmsInvoiceTable_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    fbTypeFilter = freightBillTypeFilter.valueStr();

    invoiceRecIds = TMSInvoiceTable::filterInvoices(fbTypeFilter);

    tmsInvoiceTable_ds.query().dataSourceTable(tableNum(TMSInvoiceTable)).range(2).value(invoiceRecIds);
    tmsInvoiceTable_ds.executeQuery();

    return ret;
}

