[Form]
public class TMSLoadBuildTemplates extends FormRun
{
    SysQueryRun                 criteriaQueryRun;
    TMSLoadBuildWorkbenchForm   tmsLoadBuildWorkbenchForm;
    protected SysFormEnumComboBox supplyDemandTypeComboBox;
    protected boolean isSysFormEnumComboBoxFlightEnabled = WHSLoadBuildTemplatesSupplyDemandTypeFlight::instance().isEnabled();

}

/// <summary>
/// Enables or disables the destination address fields.
/// </summary>
/// <param name="_enabled">
/// If true, enable the fields, disable otherwise.
/// </param>
public void enableDestinationAddress(boolean _enabled)
{
    TMSLoadBuildTemplate_DestinationFromPostalCode.enabled(_enabled);
    TMSLoadBuildTemplate_DestinationToPostalCode.enabled(_enabled);
    TMSLoadBuildTemplate_DestinationCountryId.enabled(_enabled);
}

[Hookable(false)]
protected void enableDestinationWarehouseAndSite(boolean _enabled)
{
    TMSLoadBuildTemplate_DestinationLocationId.enabled(_enabled);
    TMSLoadBuildTemplate_DestinationSiteId.enabled(_enabled);

    if (_enabled)
    {
        if (TMSLoadBuildTemplate_DestinationLocationId.valueStr())
        {
            this.enableDestinationAddress(false);
        }
        else
        {
            this.enableDestinationAddress(true);
        }
    }
    else
    {
        TMSLoadBuildTemplate_DestinationLocationId.text('');
        TMSLoadBuildTemplate_DestinationSiteId.text('');
        this.enableDestinationAddress(true);
    }
}

[Hookable(false)]
protected void enableFilterCodes(boolean _enable)
{
    TMSLoadBuildTemplate_FilterCode1.enabled(_enable);
    TMSLoadBuildTemplate_FilterCode2.enabled(_enable);
    TMSLoadBuildTemplate_FilterCode3.enabled(_enable);
    TMSLoadBuildTemplate_FilterCode4.enabled(_enable);
}

/// <summary>
/// Enables or disables the origin address fields.
/// </summary>
/// <param name="_enabled">
/// If true, enable the fields, disable otherwise.
/// </param>
public void enableOriginAddress(boolean _enabled)
{
    TMSLoadBuildTemplate_OriginFromPostalCode.enabled(_enabled);
    TMSLoadBuildTemplate_OriginToPostalCode.enabled(_enabled);
    TMSLoadBuildTemplate_OriginCountryId.enabled(_enabled);
}

[Hookable(false)]
protected void enableOriginWarehouseAndSite(boolean _enabled)
{
    TMSLoadBuildTemplate_OriginLocationId.enabled(_enabled);
    TMSLoadBuildTemplate_OriginSiteId.enabled(_enabled);

    if (_enabled)
    {
        if (TMSLoadBuildTemplate_OriginLocationId.valueStr())
        {
            this.enableOriginAddress(false);
        }
        else
        {
            this.enableOriginAddress(true);
        }
    }
    else
    {
        TMSLoadBuildTemplate_OriginLocationId.text('');
        TMSLoadBuildTemplate_OriginSiteId.text('');
        this.enableOriginAddress(true);
    }
}

public void run()
{
    super();

    if (isSysFormEnumComboBoxFlightEnabled)
    {
        // supplyDemandTypeComboBox is only retrieving the selected value after form run()
        this.setCriteriaQueryRun();
    }
}

public void init()
{
    if (isSysFormEnumComboBoxFlightEnabled)
    {
        this.initSupplyDemandTypeComboBox();
    }

    super();

    if (!isSysFormEnumComboBoxFlightEnabled)
    {
        this.setCriteriaQueryRun();
    }

    tmsLoadBuildWorkbenchForm = TMSLoadBuildWorkbenchForm::newParameters(this);
}

private TMSLoadBuildSupplyDemandType getLoadBuildSupplyDemandType()
{
    if (isSysFormEnumComboBoxFlightEnabled)
    {
        return tmsLoadBuildTemplate.SupplyDemandType;
    }
    return TMSLoadBuildTemplate_SupplyDemandType.selection();
}

private void initSupplyDemandTypeComboBox()
{
    supplyDemandTypeComboBox = SysFormEnumComboBox::newParameters(
        element,
        element.controlId(formControlStr(TMSLoadBuildTemplates, TMSSupplyDemandType)),
        enumNum(TMSLoadBuildSupplyDemandType),
        this.supportedSupplyDemandTypes());
}

[Hookable(true)]
protected Set supportedSupplyDemandTypes()
{
    Set supportedSupplyDemandTypes = new Set(Types::Enum);
    supportedSupplyDemandTypes.add(TMSLoadBuildSupplyDemandType::Sales);
    return supportedSupplyDemandTypes;
}

/// <summary>
/// Copies the template details into the form.
/// </summary>
/// <param name="_template">
/// The template to use as a source to populate the form.
/// </param>
public void populateForm(TMSLoadBuildTemplate _template)
{
    if (_template)
    {
        if (_template.SupplyDemandQueryRun)
        {
            this.setCriteriaQueryRun(new SysQueryRun(_template.SupplyDemandQueryRun));
        }
        else
        {
            this.setCriteriaQueryRun();
        }

        this.setAccountFromTemplate(_template);
        this.setupForm();
    }
}

/// <summary>
/// Copies the account details from the template
/// </summary>
/// <param name = "_template">
/// The template to use as a source to populate the account.
/// </param>
[Hookable(true)]
protected void setAccountFromTemplate(TMSLoadBuildTemplate _template)
{
    switch (_template.SupplyDemandType)
    {
        case TMSLoadBuildSupplyDemandType::Sales,
            TMSLoadBuildSupplyDemandType::TransferIssue,
            TMSLoadBuildSupplyDemandType::Shipment:
            CustomerAccount.text(_template.Account);
            break;
        case TMSLoadBuildSupplyDemandType::Purchase:
            VendorAccount.text(_template.Account);
            break;
    }
}

public void setCriteriaQueryRun(SysQueryRun _criteriaQueryRun = null)
{
    if (!_criteriaQueryRun)
    {
        criteriaQueryRun = TMSLoadBuildWorkbenchForm::createNewQueryRun(tmsLoadBuildTemplate.SupplyDemandType);
    }
    else
    {
        criteriaQueryRun = _criteriaQueryRun;
    }

    if (!tmsLoadBuildTemplate)
    {
        tmsLoadBuildTemplate.SupplyDemandQueryRun = criteriaQueryRun.pack();
    }

    //we want a fresh query and not the one saved in syslast value by the query form
    criteriaQueryRun.promptLoadLastUsedQuery(false);
    criteriaQueryRun.promptAllowSave(false);
}

/// <summary>
/// Sets up the form depending on the consolidation type used.
/// </summary>
public void setupForm()
{
    switch (this.getLoadBuildSupplyDemandType())
    {
        case TMSLoadBuildSupplyDemandType::Sales:
            CustomerAccount.visible(true);
            CustomerAccount.enabled(true);
            VendorAccount.visible(false);
            VendorAccount.text('');
            this.enableFilterCodes(true);
            this.enableOriginWarehouseAndSite(true);
            this.enableDestinationWarehouseAndSite(false);
            break;
        case TMSLoadBuildSupplyDemandType::Purchase:
            CustomerAccount.visible(false);
            CustomerAccount.text('');
            VendorAccount.visible(true);
            this.enableFilterCodes(true);
            this.enableOriginWarehouseAndSite(false);
            this.enableDestinationWarehouseAndSite(true);
            break;
        case TMSLoadBuildSupplyDemandType::TransferIssue:
            CustomerAccount.visible(false);
            CustomerAccount.text('');
            VendorAccount.visible(false);
            VendorAccount.text('');
            this.enableFilterCodes(true);
            this.enableOriginWarehouseAndSite(true);
            this.enableDestinationWarehouseAndSite(true);
            break;
        case TMSLoadBuildSupplyDemandType::Shipment:
            CustomerAccount.visible(true);
            CustomerAccount.enabled(true);
            VendorAccount.visible(false);
            VendorAccount.text('');
            this.enableFilterCodes(false);
            this.enableOriginWarehouseAndSite(true);
            this.enableDestinationWarehouseAndSite(false);
            break;
    }
}

public int task(int _p1)
{
    #Task

    int ret;

    ret = super(_p1);

    if (_p1 == #taskEditRecord || _p1 == #taskDoneEditRecord)
    {
        this.toggleControls();
    }

    return ret;
}

private void toggleControls()
{
    ActionPaneTabLine.enabled(!element.inViewMode() && TMSLoadBuildTemplate.RecId);
}

[Hookable(true)]
protected void clearAccountFields()
{
    CustomerAccount.text('');
    VendorAccount.text('');
}

public void create(boolean _append = false)
{
    super(_append);

    element.toggleControls();
    element.setCriteriaQueryRun();
}

public int active()
{
    int ret;

    ret = super();

    element.toggleControls();

    element.clearAccountFields();

    element.setCriteriaQueryRun();

    element.setupForm();

    element.populateForm(tmsLoadBuildTemplate);

    return ret;
}

public void write()
{
    super();

    element.toggleControls();
}

public void executeQuery()
{
    if (isSysFormEnumComboBoxFlightEnabled)
    {
        SysQuery::findOrCreateRange(
            this.query().dataSourceTable(tableNum(TMSLoadBuildTemplate)),
            fieldNum(TMSLoadBuildTemplate, SupplyDemandType))
            .value(queryValue(supplyDemandTypeComboBox.selection()));
    }

    super();

    element.setupForm();
}

public void initValue()
{
    super();

    if (isSysFormEnumComboBoxFlightEnabled)
    {
        tmsLoadBuildTemplate.SupplyDemandType = supplyDemandTypeComboBox.selection();
    }
}

void setSequence(boolean _up = false)
{
    TMSLoadBuildTemplateLoadTemplate tmsLoadBuildTemplateLoadTemplateLocal;

    tmsLoadBuildTemplateLoadTemplateLocal = this.cursor();
    tmsLoadBuildTemplateLoadTemplateLocal = tmsLoadBuildTemplateLoadTemplateLocal.sequenceArrange(_up);

    this.executeQuery();
    this.positionToRecord(tmsLoadBuildTemplateLoadTemplateLocal);
}

public void delete()
{
    super();

    TMSLoadBuildWorkbenchForm::validateMoveButtons(
        TMSLoadBuildTemplateLoadTemplate_ds,
        ButtonMoveUpLine,
        ButtonMoveDownLine);
}

public int active()
{
    int ret;

    ret = super();

    TMSLoadBuildWorkbenchForm::validateMoveButtons(
        TMSLoadBuildTemplateLoadTemplate_ds,
        ButtonMoveUpLine,
        ButtonMoveDownLine);

    return ret;
}

public void initValue()
{
    super();

    tmsLoadBuildTemplateLoadTemplate.Sequence = TMSLoadBuildTemplateLoadTemplate::getNextSequenceNumber(tmsLoadBuildTemplateLoadTemplate);
}

void clicked()
{
    super();

    element.setCriteriaQueryRun(criteriaQueryRun);

    criteriaQueryRun.prompt();

    if (tmsLoadBuildTemplate)
    {
        tmsLoadBuildTemplate.SupplyDemandQueryRun = criteriaQueryRun.pack();
        tmsLoadBuildTemplate.doUpdate();
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    TMSLoadBuildTemplate_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.setupForm();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (tmsLoadBuildTemplate.SupplyDemandType == TMSLoadBuildSupplyDemandType::Sales
        || tmsLoadBuildTemplate.SupplyDemandType == TMSLoadBuildSupplyDemandType::Shipment)
    {
        tmsLoadBuildTemplate.Account = CustomerAccount.valueStr();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (tmsLoadBuildTemplate.SupplyDemandType == TMSLoadBuildSupplyDemandType::Purchase)
    {
        tmsLoadBuildTemplate.Account = VendorAccount.valueStr();
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    tmsLoadBuildTemplate.OriginLocationId = tmsLoadBuildWorkbenchForm.clearInvalidInventLocationIdFilter(TMSLoadBuildTemplate_OriginLocationId.valueStr(), this.valueStr());

    return ret;
}

