public void init()
{
    this.processRouteFilter();

    super();

    originAddressController = LogisticsPostalAddressFormHandler::newParameters(tmsRouteSegment_ds, tmsRouteSegmentOriginAddress_ds);
    originAddressController.callerUpdateQuery(fieldNum(TMSRouteSegment,OriginLocation));
    originAddressController.parmDestination(false);
    destinationAddressController = LogisticsPostalAddressFormHandler::newParameters(tmsRouteSegment_ds, tmsRouteSegmentDestinationAddress_ds);
    destinationAddressController.callerUpdateQuery(fieldNum(TMSRouteSegment, DestinationLocation));
    destinationAddressController.parmDestination(true);

    this.setReturnShipCarrierTrackingNumVisibility();
}

