}

NumberSeqFormHandler numberSeqFormHandler_Route()
{
    if (!numberSeqFormHandler_Route)
    {
        numberSeqFormHandler_Route = NumberSeqFormHandler::newForm(TMSParameters::numRefTMSRouteCode().NumberSequenceId,
                                                                   element,
                                                                   tmsRoute_DS,
                                                                   fieldNum(TMSRoute, RouteCode));
    }

    return numberSeqFormHandler_Route;
}

private void processRouteFilter()
{
    TMSRouteSegmentLoadLine segmentLine;
    SalesTable              salesTable;
    SalesLine               salesLine;
    PurchTable              purchTable;
    PurchLine               purchLine;
    int                     counter;

    switch (element.args().dataset())
    {
        case tableNum(WHSLoadTable):
            loadTable   = element.args().record();
            routeFilter = loadTable.tmsDisplayRouteCode();
            break;

        case tableNum(SalesTable):
            salesTable  = element.args().record();

            while select RouteCode from segmentLine
                group by RouteCode
                join InventTransId, SalesId from salesLine
                where salesLine.InventTransId   == segmentLine.InventTransId &&
                        salesLine.SalesId         == salesTable.SalesId
            {
                if (counter > 0)
                {
                    routeFilter += ', ';
                }
                routeFilter += segmentLine.RouteCode;
                ++counter;
            }
            break;

        case tableNum(PurchTable):
            purchTable = element.args().record();

            while select RouteCode from segmentLine
                    group by RouteCode
                    join InventTransId, PurchId from purchLine
                    where purchLine.InventTransId   == segmentLine.InventTransId &&
                          purchLine.PurchId         == purchTable.PurchId
            {
                if (counter > 0)
                {
                    routeFilter += ', ';
                }
                routeFilter += segmentLine.RouteCode;
                ++counter;
            }
            break;

        case tableNum(TMSRoute):
            TMSRoute routeCriteria = element.args().record() as TMSRoute;

            if (routeCriteria)
            {
                routeFilter = routeCriteria.RouteCode;
            }
            break;
    }
}

TMSActualWeight routeShipmentWeight()
{
    TMSActualWeight             ret;
    TMSRouteSegmentShipment     shipment;

    while select ActualWeight from shipment
        where shipment.RouteCode == tmsRoute.RouteCode
    {
        ret += shipment.ActualWeight;
    }

    return ret;
}

public void run()
{
    Imagelist           list        = new Imagelist(Imagelist::smallIconWidth(), Imagelist::smallIconHeight());
    FormWindowControl   override    = element.control((element.controlId(formControlStr(TMSRouteWorkbench, WindowOverride))));

    list.add(new Image(7850));
    override.imageList(list);

    super();

    if (element.args().menuItemName() == menuitemDisplayStr(TMSRouteWorkbench_ManualRoute))
    {
        TMSRoute_ds.positionToRecordByValue(fieldNum(TMSRoute, RouteCode), element.args().parm());

        element.callRouteRateDetails();
    }
}

public void executeQuery()
{
    if (routeFilter)
    {
        qbrRouteCode.value(routeFilter);
    }

    super();
}

public void init()
{
    super();

    if (routeFilter)
    {
        qbrRouteCode = this.query().dataSourceTable(tableNum(TMSRoute)).addRange(fieldNum(TMSRoute, RouteCode));
    }

    this.cacheAddMethod(tableMethodStr(TMSRoute, displayTotalMiles));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayPickUpPostalCode));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayDropOffPostalCode));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayTotalRate));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayTotalCustomerRate));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayTotalShipperRate));
    this.cacheAddMethod(tableMethodStr(TMSRoute, displayTotalTransitTime));
}

