void create(boolean append = false,
            boolean extern = false)  // If created externally
{
    tmsRoute_RouteCode.setFocus();

    element.numberSeqFormHandler_Route().formMethodDataSourceCreatePre();

    super(append);

    if (!extern)
    {
        element.numberSeqFormHandler_Route().formMethodDataSourceCreate();
    }

    if (loadTable)
    {
        tmsRoute.createFromLoad(loadTable);

        if (routeFilter)
        {
            routeFilter = routeFilter + ',' + tmsRoute.RouteCode;
        }

        // For the form to filter correctly with the new route associated to the load.
        element.args().record().reread();
        this.research();
    }
}

public int active()
{
    int             ret;
    boolean         confirmed = tmsRoute.RouteStatus == TMSRouteStatus::Confirmed;

    ret = super();

    tmsRoute_ds.allowEdit(!confirmed);
    tmsRoute_ds.allowDelete(!confirmed);
    tmsRouteSegment_ds.allowEdit(!confirmed);
    tmsRouteSegment_ds.allowDelete(!confirmed);
    tmsRouteSegmentLoad_ds.allowDelete(!confirmed);
    tmsRouteSegmentShipment_ds.allowDelete(!confirmed);
    tmsInvoiceJournal.enabled(TMSFreightBillDetail::existsInvoiceJournal(tmsRoute.RouteCode));
    overrideTabPage.visible(tmsRoute.displayHasChargeOverride());

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        originAddressController.callerActive();
        originAddressController.updateButtons(newAddressOrigin, editAddressOrigin, clearAddressOrigin, mapButtonOrigin);
        destinationAddressController.callerActive();
        destinationAddressController.updateButtons(newAddressDestination, editAddressDestination, clearAddressDestination, mapButtonDestination);
    }

    return ret;
}

public void initValue()
{
    super();

    tmsRouteSegment.Sequence = (select Sequence
                                   from tmsRouteSegment
                                   order by Sequence desc
                                   where tmsRouteSegment.RouteCode == tmsRoute.RouteCode &&
                                         tmsRouteSegment.Manual == NoYes::Yes).Sequence + 100;
    tmsRouteSegment.Manual   = NoYes::Yes;
}

