public void modified()
{
    LogisticsPostalAddress destinationAddress;

    super();

    tmsRouteSegment.DestinationLocation = TMSHubMaster::find(tmsRouteSegment.DestinationHubCode).Location;

    destinationAddress = LogisticsPostalAddress::findByLocation(tmsRouteSegment.DestinationLocation);

    tmsRouteSegmentDestinationAddress.data(destinationAddress);
    destinationAddressController.callerActive();
    destinationAddressController.updateButtons(newAddressDestination, editAddressDestination, clearAddressDestination, mapButtonDestination);
    tmsRouteSegmentDestinationAddress_ds.refresh();
}

