void clicked()
{
    str range;

    for (TMSRoute rec = tmsRoute_ds.getFirst(true) ? tmsRoute_ds.getFirst(true) : tmsRoute_ds.cursor(); rec; rec = tmsRoute_ds.getNext())
    {
        range = range == '' ? rec.RouteCode : range + ',' + rec.RouteCode;
    }

    Args  args = new Args(ssrsReportStr(TMSRoute, Report));
    args.menuItemType(MenuItemType::Display);
    args.menuItemName(menuItemDisplayStr(TMSRouteWorkbench));
    args.caller(this);
    args.record(TmsRoute);
    args.parm(range);

    var srsReportRun = TMSRouteController::construct();
    var reportName = PrintMgmtDocType::construct(PrintMgmtDocumentType::TMSRoute).getDefaultReportFormat();
    srsReportRun.parmReportName(reportName);
    srsReportRun.parmArgs(args);
    srsReportRun.parmShowDialog(false);
    srsReportRun.startOperation();
}

public void clicked()
{
    // New segments cannot be added to a scheduledRoute once it has been generated.
    if (TMSRoute.RouteSchedule != 0)
    {
        throw error("@TRX:ErrorCannotAddNewSegmentsToScheduledRoute");
    }

    super();
}

public boolean modified()
{
    boolean ret;

    if (tmsRouteSegment.orig().CarrierCode != tmsRouteSegment.CarrierCode)
    {
        tmsRouteSegment.CarrierServiceCode = '';
    }

    ret = super();

    return ret;
}

