[Form]
public class TMSTransportationTender extends FormRun implements OfficeIGeneratedWorkbookCustomExporter, OfficeIMenuCustomizer
{
    private const str ExportTenderAndHistoryToExcelOptionId = 'TenderAndHistoryCustom';

    private str transRefType;
    Common source;

    private QueryBuildRange qbrTenderStatus;
    private str             tenderStatusHideAcceptedAndDeniedFilterValues;

}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(TMSTransportationTenderEntity), ExportTenderAndHistoryToExcelOptionId);
    exportOption.displayName("@TRX:TMSTransportationTenderHeaderAndHistory");
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportTenderAndHistoryToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(TMSTransportationTenderEntity));

            var tenderEntityInfo = context.findEntityByName(dataentityviewstr(TMSTransportationTenderEntity));

            var tenderFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(TMSTransportationTenderEntity));
            var tenderIdFilter = tenderFilterBuilder.areEqual(fieldStr(TMSTransportationTenderEntity, TenderId), TMSTransportationTender.TransportationOrderNum);

            tenderEntityInfo.filter(tenderFilterBuilder.and(tenderFilterBuilder.companyFilter(), tenderIdFilter));

            var historyEntityInfo = context.addEntityDefault(dataentityviewstr(TMSTransportationTenderHistoryEntity));

            var historyFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(TMSTransportationTenderHistoryEntity));
            var historyTenderIdFilter = historyFilterBuilder.areEqual(fieldStr(TMSTransportationTenderHistoryEntity, TransportationTenderId), TMSTransTenderHistory.TransportationOrderNum);

            historyEntityInfo.filter(historyFilterBuilder.and(historyFilterBuilder.companyFilter(), historyTenderIdFilter));
            break;
    }

    return context;
}

public void enableDisableTransTenderFields()
{
    TMSTenderStatus     tenderStatus    = tmsTransportationTender.TenderStatus;
    TMSTenderRefType    tenderRefType   = tmsTransportationtender.TenderRefType;

    submit.enabled(((logisticsPostalAddressOrigin.CountryRegionId       || logisticsPostalAddressOrigin.ZipCode)        &&
                   (logisticsPostalAddressDestination.CountryRegionId   || logisticsPostalAddressDestination.ZipCode))  &&
                   (tmsTransportationTender.CarrierCode                 && tmsTransportationTender.CarrierServiceCode)  &&
                   tenderStatus < TMSTenderStatus::Submitted);

    confirmButton.enabled(tenderStatus == TMSTenderStatus::Submitted);
    accept.enabled(tenderStatus == TMSTenderStatus::Confirmed);
    deny.enabled(tenderStatus == TMSTenderStatus::Confirmed);
    tmsTransportationTender_Sequence_Edit.enabled(tenderRefType == TMSTenderRefType::RouteSegment ||
                                                tenderRefType == TMSTenderRefType::RouteSegLoad);
    tmsTransportationTender_LoadId_Edit.enabled(tenderRefType == TMSTenderRefType::RouteSegLoad);

    switch (tenderStatus)
    {
        case TMSTenderStatus::Accepted:
            tmsTransportationTender_ds.allowEdit(false);
            tmsTransportationTender_ds.allowDelete(false);
            break;

        case TMSTenderStatus::Denied:
            tmsTransportationTender_ds.allowEdit(false);
            tmsTransportationTender_ds.allowDelete(false);
            break;

        default:
            tmsTransportationTender_ds.allowEdit(true);
            tmsTransportationTender_ds.allowDelete(true);
            break;
    }
}

public void init()
{
    super();

    if (element.args() && element.args().dataset())
    {
        source = element.args().record();
        TypeFilter.enabled(false);
    }

    if (element.args().dataset() == tableNum(TMSTransportationTenderStaging)
        || element.args().dataset() == tableNum(TMSTransportationTenderHistoryStaging))
    {
        HideAcceptedDenied.value(0);
        this.parmTenderStatusHideAcceptedAndDeniedFilterValues('');
    }
    else
    {
        this.parmTenderStatusHideAcceptedAndDeniedFilterValues(con2Str([SysQuery::valueNot(TMSTenderStatus::Accepted), SysQuery::valueNot(TMSTenderStatus::Denied)]));
    }
}

private str parmTenderStatusHideAcceptedAndDeniedFilterValues(str _tenderStatusHideAcceptedAndDeniedFilterValues = tenderStatusHideAcceptedAndDeniedFilterValues)
{
    tenderStatusHideAcceptedAndDeniedFilterValues = _tenderStatusHideAcceptedAndDeniedFilterValues;

    return tenderStatusHideAcceptedAndDeniedFilterValues;
}

public void close()
{
    super();

    if (source)
    {
        FormDataSource fds = source.dataSource();
        if (fds)
        {
            List dsList = fds.formRun().rootFormDataSources();

            if(dsList && dsList.elements() > 0)
            {
                ListEnumerator dsListEnumerator = dsList.getEnumerator();

                while(dsListEnumerator.moveNext())
                {
                    FormDataSource formDS;
                    formDS = dsListEnumerator.current();
                    if(formDS.table() == tableNum(TMSRoute))
                    {
                        formDS.research(true);
                        break;
                    }
                }
            }
        }
    }
}

public void write()
{
    TMSTransportationTender orig = tmsTransportationTender.orig();
    var tmsTransportTender = new TmsTransportTender();

    if (!orig && !source)
    {
        tmsTransportTender.validateOriginDestination(TMSTransportationTender);
    }
    super();

    if (!orig && !source)
    {
        tmsTransportationTender_ds.research();
     }
}

public int active()
{
    int ret;

    ret = super();

    element.enableDisableTransTenderFields();

    return ret;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceName(this.name());
    qbds.addRange(fieldNum(TMSTransportationTender, TenderRefType));

    qbrTenderStatus = qbds.addRange(fieldNum(TMSTransportationTender, TenderStatus));
    qbrTenderStatus.value(element.parmTenderStatusHideAcceptedAndDeniedFilterValues());
    qbrTenderStatus.status(RangeStatus::Hidden);
}

public void create(boolean _append = false)
{
    if (source)
    {
        TMSTransportationTender tmsTransportationTenderLocal;

        tmsTransportationTenderLocal.TenderStatus = TMSTenderStatus::Pending;
        if (TmsTransportTender::createTransTenderRecord(source, tmsTransportationTenderLocal))
        {
            tmsTransportationTender_ds.research();
        }
        else
        {
            this.allowCreate(false);
        }
    }
    else
    {
        super(_append);
    }
}

public void executeQuery()
{
    if (HideAcceptedDenied.value())
    {
        qbrTenderStatus.value(element.parmTenderStatusHideAcceptedAndDeniedFilterValues());
    }
    else
    {
        qbrTenderStatus.value(SysQuery::valueUnlimited());
    }

    super();
}

