public void jumpRef()
{
    Args         args = new Args();
    MenuFunction menuFunction;

    if (tmsTransportationTender.TenderRefType == TMSTenderRefType::RouteSegLoad ||
        tmsTransportationTender.TenderRefType == TMSTenderRefType::RouteSegment)
    {
        args.caller(element);
        args.record(TMSRouteSegment::find(tmsTransportationTender.RefId, tmsTransportationTender.Sequence));
        menuFunction = new MenuFunction(menuitemDisplayStr(TMSRouteWorkbench), MenuItemType::Display);
        menuFunction.run(args);
    }
    else
    {
        super();
    }
}

