public void modified()
{
    boolean enableSequence;
    boolean enableLoadId;

    super();

    switch (tmsTransportationTender.TenderRefType)
    {
        case TMSTenderRefType::RouteSegment:
            enableSequence = true;
            break;

        case TMSTenderRefType::RouteSegLoad:
            enableSequence = true;
            enableLoadId = true;
            break;
    }

    tmsTransportationTender.RefId = '';
    tmsTransportationTender_Sequence_Edit.enabled(enableSequence);
    tmsTransportationTender_LoadId_Edit.enabled(enableLoadId);

    tmsTransportationTender_ds.refresh();
}

