void clicked()
{
    Args args = new Args();
    args.menuItemType(MenuItemType::Display);
    args.menuItemName(menuItemDisplayStr(TMSTransportationTender));
    args.record(TmsTransportationTender);

    var srsReportRun = TMSLoadTenderController::construct();
    var reportName = PrintMgmtDocType::construct(PrintMgmtDocumentType::TMSLoadTender).getDefaultReportFormat();
    srsReportRun.parmReportName(reportName);
    srsReportRun.parmArgs(args);
    srsReportRun.parmShowDialog(false);
    srsReportRun.startOperation();
}

