public boolean modified()
{
    boolean ret;

    ret = super();

    transRefType = typeFilter.valueStr();

    if (transRefType == enum2str(TMSTransportTypeFilter::None))
    {
        transRefType = '';
    }

    tmsTransportationTender_ds.query().dataSourceTable(tableNum(TMSTransportationTender)).range(1).value(transRefType);
    tmsTransportationTender_ds.executeQuery();

    return ret;
}
