/// <summary>
/// Disable irrelevant data sources to reduce data base query result size.
/// </summary>
private void disableIrrelevantDataSource()
{
    Map mapping = element.initDataSourceActiveMap();
    QueryBuildDataSource parentQbds = TaxAccountGroup_ds.query().dataSourceNo(1);

    for (int childQbdsNum = 1; childQbdsNum <= parentQbds.childDataSourceCount(); childQbdsNum++)
    {
        QueryBuildDataSource chldQbds = parentQbds.childDataSourceNo(childQbdsNum);
        var dsName = chldQbds.name();
        if (mapping.exists(chldQbds.name()))
        {
            chldQbds.enabled(mapping.lookup(chldQbds.name()));
        }
    }
}

private Map initDataSourceActiveMap()
{
    Map result = new Map(Types::String, Types::AnyType); // Using query builder datasource name as key.
    result.add('PennyDifferenceCustomerLedgerDimension', false); // Table field visible = false
    result.add('PennyDifferenceVendorLedgerDimension', false); // Table field visible = false
    result.add('TaxFreePercentLedgerDimension', false); // Table field visible = false

    result.add('TaxIncomingLedgerDimension', TaxAccountIncomingGrid.visible());
    result.add('TaxUseTaxLedgerDimension', TaxAccountUseTaxGrid.visible());

    // handled in element.showAccounts()
    result.add('Ref_DimensionAttributeValueCombination_TaxInterimTransitLedgerDimension', TaxAccountGroup_TaxInterimTransitLedgerDimension.visible());
    // copied from element.showAccounts()
    result.add('Ref_DimensionAttributeValueCombination_TaxNondeductibleTaxLedgerDimension', TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(true));

    TaxSpecificExchangeRateMenuItemDisplayToggle taxSpecificExchangeRateMenuItemDisplayToggle = new TaxSpecificExchangeRateMenuItemDisplayToggle();
    result.add('Ref_DimensionAttributeValueCombination_TaxIncomingDifferenceLedgerDimension', taxSpecificExchangeRateMenuItemDisplayToggle.isEnabled());
    result.add('Ref_DimensionAttributeValueCombination_TaxIncomingDiffOffsetLedgerDimension', taxSpecificExchangeRateMenuItemDisplayToggle.isEnabled());
    result.add('Ref_DimensionAttributeValueCombination_TaxOutgoingDifferenceLedgerDimension', taxSpecificExchangeRateMenuItemDisplayToggle.isEnabled());
    result.add('Ref_DimensionAttributeValueCombination_TaxOutgoingDiffOffsetLedgerDimension', taxSpecificExchangeRateMenuItemDisplayToggle.isEnabled());

    return result;
}

void showAccounts()
{
    if (TaxParameters::find().PurchTaxOnOperations)
    {
        taxAccountIncomingGrid.visible(false);
        taxAccountUseTaxGrid.visible(false);
    }

    TaxAccountGroup_TaxInterimTransitLedgerDimension.visible(TaxIntegrationUtils::isTaxInTransferOrderEnabled());
    // <GJP>
    if (TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(true))
    {
        TaxAccountGroup_ds.object(fieldNum(TaxLedgerAccountGroup, TaxNondeductibleTaxLedgerDimension)).visible(true);
    }
    // </GJP>
}
