public void adjustDesign()
{
    boolean enableInvoiceDate = true;
    boolean isNFE = FiscalDocModel_BR::find(taxFiscalDocument_BR.Model).IsForNFeFederal;

    //Adjusts header fields based on tax fiscal document type
    if (taxFiscalDocument_BR.TaxFiscalDocumentType == TaxFiscalDocumentType_BR::ICMSInstallment)
    {
        LineViewFiscalDocumentSeries.enabled(false);
        HeaderViewFiscalDocumentSeries.enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, FiscalDocumentType)).enabled(true);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Direction)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, InvoiceNumber)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Model)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Series)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Specie)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccountType)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccessKey)).enabled(false);
    }
    else
    {
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Direction)).enabled(true);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccountType)).enabled(true);

        if (taxFiscalDocument_BR.Direction == FiscalDocDirection_BR::Incoming)
        {
            LineViewFiscalDocumentSeries.enabled(!isNFE);
            HeaderViewFiscalDocumentSeries.enabled(!isNFE);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, FiscalDocumentType)).enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, InvoiceNumber)).enabled(!isNFe);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Model)).enabled(true);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Series)).enabled(!isNFE);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Specie)).enabled(!isNFE);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccessKey)).enabled(isNFE);
        }
        else
        {
            LineViewFiscalDocumentSeries.enabled(false);
            HeaderViewFiscalDocumentSeries.enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, FiscalDocumentType)).enabled(true);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, InvoiceNumber)).enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Model)).enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Series)).enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, Specie)).enabled(false);
            taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccessKey)).enabled(false);
        }
    }

    //adjust based on status
    switch (taxFiscalDocument_BR.Status)
    {
        case TaxFiscalDocumentStatus_BR::Open:
            buttonPost.enabled(true);
            dropDialogCancel.enabled(false);
            transactVoucher.enabled(false);
            taxFiscalDocument_BR_ds.allowEdit(true);
            taxFiscalDocument_BR_ds.allowDelete(true);
            taxFiscalDocumentLine_BR_ds.allowEdit(true);
            taxFiscalDocumentLine_BR_ds.allowDelete(true);
            taxFiscalDocumentLine_BR_ds.allowCreate(true);
            AddressActionPane.enabled(true);
            break;
        case TaxFiscalDocumentStatus_BR::Posted:
            buttonPost.enabled(false);
            if (taxFiscalDocument_BR.TaxFiscalDocToCancelRecID)
            {
                dropDialogCancel.enabled(false);
            }
            else
            {
                dropDialogCancel.enabled(true);
            }
            transactVoucher.enabled(true);
            taxFiscalDocument_BR_ds.allowEdit(false);
            taxFiscalDocument_BR_ds.allowDelete(false);
            taxFiscalDocumentLine_BR_ds.allowEdit(false);
            taxFiscalDocumentLine_BR_ds.allowCreate(false);
            taxFiscalDocumentLine_BR_ds.allowDelete(false);
            AddressActionPane.enabled(false);
            break;
        default:
            buttonPost.enabled(false);
            dropDialogCancel.enabled(false);
            transactVoucher.enabled(true);
            taxFiscalDocument_BR_ds.allowEdit(false);
            taxFiscalDocument_BR_ds.allowDelete(false);
            taxFiscalDocumentLine_BR_ds.allowEdit(false);
            taxFiscalDocumentLine_BR_ds.allowCreate(false);
            taxFiscalDocumentLine_BR_ds.allowDelete(false);
            AddressActionPane.enabled(false);
            break;
    }

    // Special ajustments for cancelling documents, regardless of state
    if (TaxFiscalDocument_BR.TaxFiscalDocToCancelRecID)
    {
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, TaxFiscalDocumentType)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccountType)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccountNum)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, CurrencyCode)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, LogisticsPostalAddress)).enabled(false);
        // following fields were already set according to tax document type or status; override for cancelling documents
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, InvoiceNumber)).enabled(false);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, FiscalDocumentType)).enabled(false);
        TaxFiscalDocumentLine_BR_ds.allowEdit(false);
        TaxFiscalDocumentLine_BR_ds.allowDelete(false);
        AddressActionPane.enabled(false);
    }
    else
    {
        if (BrazilParameters::find().ChronologicalInvoiceNumbering &&
            taxFiscalDocument_BR.FiscalDocumentType &&
            FiscalDocumentType_BR::findRecId(taxFiscalDocument_BR.FiscalDocumentType).InvoiceIssueDate)
        {
            enableInvoiceDate = false;
        }
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, InvoiceDate)).enabled(enableInvoiceDate);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, TaxFiscalDocumentType)).enabled(true);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, AccountNum)).enabled(true);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, CurrencyCode)).enabled(true);
        taxFiscalDocument_BR_ds.object(fieldNum(TaxFiscalDocument_BR, LogisticsPostalAddress)).enabled(true);
    }

    //adjust cancel tab page
    if (taxFiscalDocument_BR.Status == TaxFiscalDocumentStatus_BR::Canceled ||
        taxFiscalDocument_BR.Status == TaxFiscalDocumentStatus_BR::CancelInProgress ||
        taxFiscalDocument_BR.TaxFiscalDocToCancelRecID != 0)
    {
        HeaderCancellationTab.visible(true);
    }
    else
    {
        HeaderCancellationTab.visible(false);
    }

    if (taxFiscalDocument_BR.RecId)
    {
        buttonHeaderFiscalDocumentText.enabled(true);
    }
    else
    {
        buttonHeaderFiscalDocumentText.enabled(false);
    }
}

private void printInvoice(PrintCopyOriginal _action)
{
    TaxFiscalDocument_BR            taxFiscalDocument;
    RecordSortedList                fiscalDocumentList = new RecordSortedList(tableNum(FiscalDocument_BR));
    FiscalDocumentPrintJob_BR       printJob = new FiscalDocumentPrintJob_BR();

    for (taxFiscalDocument = getFirstSelection(TaxFiscalDocument_BR_DS);
    taxFiscalDocument;
    taxFiscalDocument = TaxFiscalDocument_BR_DS.getNext())
    {
        fiscalDocumentList.ins(FiscalDocument_BR::findByRef(taxFiscalDocument.TableId, taxFiscalDocument.RecId));
    }

    printJob.printFiscalDocuments(fiscalDocumentList, _action);
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = postalAddressLocation)
{
    postalAddressLocation = _postalAddressLocation;
    taxFiscalDocument_BR.LogisticsPostalAddress = LogisticsPostalAddress::findByLocation(postalAddressLocation).RecId;
    return postalAddressLocation;
}

