public int active()
{
    int ret = super();
    element.adjustDesign();

    if (taxFiscalDocument_BR.Status == TaxFiscalDocumentStatus_BR::Canceled ||
        taxFiscalDocument_BR.Status == TaxFiscalDocumentStatus_BR::CancelInProgress)
    {
        cancelingRecIDQBR.value(SysQuery::valueUnlimited());
        cancelingRefRecIdQBR.value(int642str(taxFiscalDocument_BR.RecId));
        cancelingTaxFiscalDocument_BR_ds.executeQuery();
    }
    else if (taxFiscalDocument_BR.TaxFiscalDocToCancelRecID)
    {
        cancelingRecIDQBR.value(int642str(taxFiscalDocument_BR.TaxFiscalDocToCancelRecID));
        cancelingRefRecIdQBR.value(SysQuery::valueEmptyString());
        cancelingTaxFiscalDocument_BR_ds.executeQuery();
    }

    return ret;
}

