public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    Common entity;

    if (taxFiscalDocument_BR.TaxFiscalDocumentType == TaxFiscalDocumentType_BR::ICMSInstallment)
    {
        entity = CompanyInfo::find();
    }
    else if (taxFiscalDocument_BR.AccountType == CustVendType_BR::Customer)
    {
        entity = CustTable::find(taxFiscalDocument_BR.AccountNum);
    }
    else
    {
        entity = VendTable::find(taxFiscalDocument_BR.AccountNum);
    }

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
        entity, [LogisticsLocationRoleType::Invoice], false);
    return ret;
}

