public void lookup(FormControl _formControl, str _filterStr)
{
    TaxFiscalValue_BR taxFiscalValue = TaxFiscalValue_BR::WithCreditDebit;

    if (taxFIscalDocument_BR.TaxFiscalDocumentType == TaxFiscalDocumentType_BR::ICMSTaxTransfer)
    {
        LogisticsPostalAddress fiscalEstablishmentPostalAddress = FiscalEstablishment_BR::findById(taxFIscalDocument_BR.FiscalEstablishmentId).postalAddress();
        if (FBParametersPerState_BR::find(fiscalEstablishmentPostalAddress).EnableDocumentAdjustment)
        {
            taxFiscalValue = TaxFiscalValue_BR::WithoutCreditDebitOther;
        }
    }

    TaxTable::lookupTaxCode_BR(_formControl, TaxType_BR::ICMS, taxFiscalValue);
}

public void init()
{
    super();
    cancelingRefRecIdQBR = cancelingTaxFiscalDocument_br_ds.query().dataSourceNo(1).addRange(fieldNum(TaxFiscalDocument_BR, TaxFiscalDocToCancelRecID));
    cancelingRecIDQBR = cancelingTaxFiscalDocument_br_ds.query().dataSourceNo(1).addRange(fieldNum(TaxFiscalDocument_BR, RecId));
}

/// <summary>
/// Refresh the form for recently added canceling fiscal document.
/// </summary>
/// <param name="_formRun">
/// The object of dialog form.
/// </param>
public void dialogClosed(xFormRun _formRun)
{
    Object  formRunObj;

    formRunObj = _formRun as Object;

    if (_formRun.closedOk())
    {
        element.args().lookupRecord(taxFiscalDocument_BR.getCancelingTaxFiscalDocument());
        taxfiscaldocument_br_ds.research(false);
    }

    super(_formRun);
}

