public void init()
{
    super();

    // Initialize form view model
    taxGroupViewModel = taxGroupViewModel::construct();
    taxGroupViewModel.parmTaxGroupHeadingDataSource(taxGroupHeading_ds);
    taxGroupViewModel.parmRecalcTaxForTaxGroupControl(RecalcTaxForTaxGroup);
    taxGroupViewModel.init();
}

public void updateDesign_IN()
{
    TaxParameters taxParameters = TaxParameters::find();
    if (TaxParameters::find().vat_in)
    {
        taxGroupData_ds.object(fieldnum(TaxGroupData, vatScheduleName_IN)).visible(true);
    }
    else
    {
        taxGroupData_ds.object(fieldnum(TaxGroupData, vatScheduleName_IN)).visible(false);
    }

    if (TaxTable::find(taxGroupData.TaxCode).TaxType_IN == TaxType_IN::VAT)
    {
        taxGroupData_ds.object(fieldnum(TaxGroupData, vatScheduleName_IN)).enabled(true);
    }
    else
    {
        taxGroupData_ds.object(fieldnum(TaxGroupData, vatScheduleName_IN)).enabled(false);
    }

    showPreferentialValue_IN.visible(taxParameters.Customs_IN);
}

private void updateDesignForVATDueDateCustomizedFields()
{
    boolean isVATDueDateCustomized = (TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled()
        && TaxVATDueDateCustomizeFeatureExposure::isEnabled()
        && taxGroupHeading.FillVATDueDate_W == FillVATDueDate_W::Customized);
    taxGroupHeading_ds.object(fieldNum(TaxGroupHeading, FillVATDueDateBasedOn)).visible(isVATDueDateCustomized);
    taxGroupHeading_ds.object(fieldNum(TaxGroupHeading, FillVATDueDatePeriod)).visible(isVATDueDateCustomized);
    taxGroupHeading_ds.object(fieldNum(TaxGroupHeading, FillVATDueDatePeriodNumber)).visible(isVATDueDateCustomized);
}

/// <summary>
/// Updates form design for tax service.
/// </summary>
private void updateDesignForTaxService()
{
    boolean isTaxServiceEnabled = Tax::isTaxIntegrationEnabledForTaxService();

    TaxGroupHeading_ds.object(fieldNum(TaxGroupHeading, Source)).visible(isTaxServiceEnabled);
    TaxGroupHeading_ds.allowDelete(!(isTaxServiceEnabled && TaxGroupHeading.Source == TaxMasterDataSource::TaxService));
}

public int active()
{
    int ret;

    ret = super();

    //Hide CallCenterDefaults tab when RetailHideAutoSalesTaxGroupSearchFlight flight is enabled.
    CallCenterDefaults.visible(!RetailHideAutoSalesTaxGroupSearchFlight::instance().isEnabled());

    if (taxGroupHeading.TaxGroupSetup == TaxGroupSetup::TaxCode)
    {
        //setupJurisdiction.visible(false);  //Jurisdiction should be hidden
        taxGroupData_ds.allowDelete(true);
        taxGroupData_ds.allowCreate(true);
    }
    else
    {
        //setupJurisdiction.visible(true);  //Jurisdiction should be hidden
        taxGroupData_ds.allowDelete(false);
        taxGroupData_ds.allowCreate(false);
    }

    element.updateDesignForVATDueDateCustomizedFields();
    element.updateDesignForTaxService();

    return ret;
}

public int active()
{
    int ret;

    // <GIN>
