    // </GIN>
    ret = super();

    taxGroupData.enableDataSourceFields();

    // <GBR>
    TaxGroupData_ds.enableTaxationCode();
    // </GBR>

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        if (!TaxParameters::find().Excise_IN)
