    // </GIN>
    return ret;
}

public edit TaxationCode_BR editTaxationCode_BR(boolean set,
                                                TaxGroupData       _taxGroupData,
                                                TaxationCode_BR    _taxationCode)
{
    TaxationCode_BR taxationCode = _taxationCode;
    boolean validateField;

    if (set)
    {
        _taxGroupData.TaxationCodeTable_BR = TaxationCodeTable_BR::find(TaxTable::find(_taxGroupData.TaxCode).TaxType_BR, taxationCode).RecId;

        if (taxationCode && !_taxGroupData.TaxationCodeTable_BR)
        {
            validateField = checkFailed(strfmt("@SYS112224", taxationCode,
                                        fieldId2pname(tableNum(TaxGroupData), fieldNum(TaxGroupData, TaxationCodeTable_BR)),
                                        tableId2pName(tableNum(TaxationCodeTable_BR))));
        }
        else
        {
            validateField = _taxGroupData.validateField(fieldNum(TaxGroupData, TaxationCodeTable_BR));
        }

        if (!validateField)
        {
            throw Exception::Warning;
        }

        _taxGroupData.modifiedField(fieldNum(TaxGroupData, TaxationCodeTable_BR));
    }
    else
    {
        taxationCode = TaxationCodeTable_BR::findRecId(_taxGroupData.TaxationCodeTable_BR).TaxationCode;
    }

    return taxationCode;
}

public void enableTaxationCode()
{
    if (!BrazilParameters::isEnabled())
    {
        return;
    }

    editTaxationCode_BR.allowEdit(TaxGroupData.ExemptTax == NoYes::Yes);

    if (!TaxGroupData.ExemptTax)
    {
        TaxGroupData.TaxationCodeTable_BR = 0;
        editTaxationCode_BR.text('');
    }
}

public void modified()
{
    super();

    TaxGroupData_ds.enableTaxationCode();
}

