/// <summary>
/// Lookup reference for City control in Dbt grid.
/// </summary>
/// <param name="_formReferenceControl">
/// FormReferenceControl.
/// </param>
/// <returns>
/// lookup result set.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Query query;
    SysReferenceTableLookup sysRefTableLookup;
    QueryBuildDataSource dsLookup;

    query = new Query();
    dsLookup = query.addDataSource(tableNum(LogisticsAddressCity));
    sysRefTableLookup = SysReferenceTableLookup::newParameters(tableNum(LogisticsAddressCity), _formReferenceControl);

    // Country
    RetailTaxFilters::UpdateQueryDataSourceForCountry(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressCity, CountryRegionId));

    // State
    RetailTaxFilters::UpdateQueryDataSourceForState(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressCity, StateId));

    // County
    RetailTaxFilters::UpdateQueryDataSourceForCounty(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressCity, CountyId));

    sysRefTableLookup.parmQuery(query);

    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressCity, Name));
    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressCity, StateId));
    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressCity, CountyId));

    return sysRefTableLookup.performFormLookup();
}

