/// <summary>
/// Lookup reference for ZipCode control in Dbt grid.
/// </summary>
/// <param name="_formReferenceControl">
/// FormReferenceControl.
/// </param>
/// <returns>
/// lookup result set.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Query query;
    SysReferenceTableLookup sysRefTableLookup;
    QueryBuildDataSource dsLookup;
    ;

    query = new Query();
    dsLookup = query.addDataSource(tableNum(LogisticsAddressZipCode));
    sysRefTableLookup = SysReferenceTableLookup::newParameters(tableNum(LogisticsAddressZipCode), _formReferenceControl);

    // Country
    RetailTaxFilters::UpdateQueryDataSourceForCountry(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressZipCode, CountryRegionId));

    // State
    RetailTaxFilters::UpdateQueryDataSourceForState(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressZipCode, State));

    // County
    RetailTaxFilters::UpdateQueryDataSourceForCounty(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressZipCode, County));

    // City
    RetailTaxFilters::UpdateQueryDataSourceForCity(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressZipCode, CityRecId));

    // District
    RetailTaxFilters::UpdateQueryDataSourceForDistrict(RetailTaxFilters, dsLookup, query, fieldnum(LogisticsAddressZipCode, District));

    sysRefTableLookup.parmQuery(query);
    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressZipCode, ZipCode));
    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressZipCode, City));
    sysRefTableLookup.addLookupfield(fieldNum(LogisticsAddressZipCode, CityAlias));

    return sysRefTableLookup.performFormLookup();
}

