[Form]
public class TaxItemGroup extends FormRun
{
    boolean isServiceTax;
    boolean isIndianTax;
    TaxParameters taxParametersLoc;
    private boolean isBRNFSEEnabled;
    #ISOCountryRegionCodes

}

public boolean canClose()
{
    boolean ret;
    // <GIN>
    #ISOCountryRegionCodes
    boolean             canClose = true;
    boolean             isIndiaRegion = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    TaxTable            serviceTaxTableLoc;

    if (isServiceTax)
    {
        if (taxParametersLoc.ServiceTaxACBasis_IN == ServiceTaxACBasis_IN::Both)
        {
            if (TaxItemGroupHeading::checkIfServiceTaxAvailable_IN(TaxItemGroupHeading.TaxItemGroup))
            {
                if (TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::None)
                {
                    canClose = false;
                }
            }
            else
            {
                if (TaxItemGroupHeading.ServiceTaxBasis_IN != ServiceTaxBasis_IN::None)
                {
                    canClose = false;
                }
            }
            serviceTaxTableLoc = TaxTable::find(TaxOnItem.TaxCode);
            if (serviceTaxTableLoc.TaxType_IN == TaxType_IN::ServiceTax )
            {
                if (TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::None)
                {
                    canClose = false;
                }
            }
        }
    }
    if (canClose == true)
    {
        ret = super();
    }
    else
    {
        ret = checkFailed("@GLS222480");
    }
    // </GIN>

    return ret;
}

public void enableButtonsActive_IN()
{
    TaxType_IN taxType;
    boolean IsTaxTypeCustom;
    taxType = TaxTable::find(taxOnItem.TaxCode).TaxType_IN;
    IsTaxTypeCustom = (taxType == TaxType_IN::Customs);

    if (taxType != TaxType_IN::None)
    {
        if (IsTaxTypeCustom)
        {
            taxOnItem_ds.object(fieldnum(TaxOnItem, CustomsChargeAsExpensePercent_IN)).enabled(true);
            taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventory_IN)).enabled(false);
            taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventoryPercent_IN)).enabled(false);
            taxOnItem_ds.object(fieldnum(TaxOnItem, AbatementPercent_IN)).enabled(true);
            taxOnItem.LoadOnInventory_IN            = NoYes::No;
            taxOnItem.LoadOnInventoryPercent_IN     = 0.00;
        }
        else
        {
            taxOnItem_ds.object(fieldnum(TaxOnItem, CustomsChargeAsExpensePercent_IN)).enabled(false);
            taxOnItem.CustomsChargeAsExpensePercent_IN     = 0.00;
            taxOnItem_ds.object(fieldnum(TaxOnItem, AbatementPercent_IN)).enabled(true);
            taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventory_IN)).enabled(true);
        }

        if (taxOnItem.LoadOnInventory_IN == NoYes::Yes)
        {
            taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventoryPercent_IN)).enabled(true);
        }
        else
        {
            taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventoryPercent_IN)).enabled(false);
        }
    }
    else
    {
        taxOnItem_ds.object(fieldnum(TaxOnItem, AbatementPercent_IN)).enabled(false);
        taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventory_IN)).enabled(false);
        taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventoryPercent_IN)).enabled(false);
        taxOnItem.AbatementPercent_IN           = 0.00;
        taxOnItem.LoadOnInventory_IN            = NoYes::No;
        taxOnItem.LoadOnInventoryPercent_IN     = 0.00;
    }

    if (!TaxParameters::find().Customs_IN)
    {
        showPreferentialValue_IN.visible(false);
    }
    else
    {
        showPreferentialValue_IN.visible(true);
    }
    formulaDesigner_IN.visible(isIndianTax);
}

public void init()
{
    super();
    //<GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        taxParametersLoc = TaxParameters::find();
        isServiceTax = (taxParametersLoc.ServiceTax_IN == NoYes::Yes);
        if (taxParametersLoc.VAT_IN
            || taxParametersLoc.Excise_IN
            || taxParametersLoc.Customs_IN
            || taxParametersLoc.SalesTax_IN
            || taxParametersLoc.ServiceTax_IN)
        {
            isIndianTax = true;
            element.setVisible_IN();
            TaxItemGroupHeading_DS.object(fieldnum(TaxItemGroupHeading, ServiceTaxBasis_IN)).allowEdit(taxParametersLoc.ServiceTaxACBasis_IN == ServiceTaxACBasis_IN::Both);
        }
    }
    //</GIN>

    isBRNFSEEnabled = BusinessDocumentParameters::isFeatureEnabled([EInvoicingFeaturesForService::BR00095]);
}

public boolean enableExemptCode()
{
    boolean enable = false;
    TaxationCodeTable_BR taxationCodeTable = TaxationCodeTable_BR::findRecId(TaxOnItem.TaxationCodeTable_BR);

    if (taxationCodeTable.TaxType == TaxType_BR::IPI
        || (taxationCodeTable.TaxType == TaxType_BR::ISS
            && isBRNFSEEnabled))
    {
        enable = true;
    }

    return enable;
}

public void setVisible_IN()
{
    taxOnItem_AbatementPercent_IN.visible(isIndianTax);
    taxOnItem_LoadOnInventory_IN.visible(isIndianTax);
    taxOnItem_LoadOnInventoryPercent_IN.visible(isIndianTax);
    taxOnItem_CustomsChargeAsExpensePercent_IN.visible(isIndianTax);
    TaxItemGroupHeading_ds.object(fieldnum(TaxItemGroupHeading, ServiceTaxBasis_IN)).visible(isServiceTax);
    TaxItemGroupHeading_ds.object(fieldnum(TaxItemGroupHeading, ReverseChargePercentage_IN)).visible(isServiceTax);
    TaxItemGroupHeading_ds.object(fieldNum(TaxItemGroupHeading, AccruePayableAtInvoicing_IN)).visible(isServiceTax);
}

/// <summary>
/// Updates form design for tax service.
/// </summary>
private void updateDesignForTaxService()
{
    boolean isTaxServiceEnabled = Tax::isTaxIntegrationEnabledForTaxService();

    TaxItemGroupHeading_ds.object(fieldNum(TaxItemGroupHeading, Source)).visible(isTaxServiceEnabled);
    TaxItemGroupHeading_ds.allowDelete(!(isTaxServiceEnabled && TaxItemGroupHeading.Source == TaxMasterDataSource::TaxService));
}

public int active()
{
    int ret;

    ret = super();

    // <GIN>
    if (isServiceTax)
    {
        TaxItemGroupHeading_ds.object(fieldnum(TaxItemGroupHeading, ReverseChargePercentage_IN)).allowEdit(TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::POTBasis);
        TaxItemGroupHeading_ds.object(fieldnum(TaxItemGroupHeading, AccruePayableAtInvoicing_IN)).allowEdit(TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::POTBasis);
    }
    // </GIN>

    element.updateDesignForTaxService();

    return ret;
}

public void modified()
{
    super();
    // <GIN>
    TaxItemGroupHeading_ds.object(fieldnum(TaxItemGroupHeading, ReverseChargePercentage_IN)).allowEdit(TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::POTBasis);
    TaxItemGroupHeading_ds.object(fieldNum(TaxItemGroupHeading, AccruePayableAtInvoicing_IN)).allowEdit(TaxItemGroupHeading.ServiceTaxBasis_IN == ServiceTaxBasis_IN::POTBasis);
    // </GIN>
}

public void delete()
{
    super();
    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        TaxItemGroupHeading_ds.reread();
        TaxItemGroupHeading_ds.refresh();
    }
    // </GIN>
}

public int active()
{
    int ret;
    ;

    ret = super();

    if (!TaxParameters::find().Excise_IN)
    {
        showConcessionalValue_IN.visible(false);
    }

    element.enableButtonsActive_IN();

    if (BrazilParameters::isEnabled())
    {
        taxOnItem_ds.object(fieldnum(TaxOnItem, TaxExemptCode)).allowEdit(element.enableExemptCode());
    }

    return ret;
}

public void modified()
{
    ;

    super();

    taxOnItem.LoadOnInventoryPercent_IN = 0.00;

    taxOnItem_ds.object(fieldnum(TaxOnItem, LoadOnInventoryPercent_IN)).enabled(taxOnItem.LoadOnInventory_IN);

    taxOnItem_ds.refresh();
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common ret = TaxationCodeTable_BR::resolveReferenceByTaxType(_formReferenceControl, TaxTable::find(TaxOnItem.TaxCode).TaxType_BR);

    if (!ret)
    {
        ret = super(_formReferenceControl);
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return TaxationCodeTable_BR::lookupReferenceByTaxType(_formReferenceControl, TaxTable::find(TaxOnItem.TaxCode).TaxType_BR);
}

public void modified()
{
    super();

    if (BrazilParameters::isEnabled())
    {
        taxOnItem_ds.object(fieldnum(TaxOnItem, TaxExemptCode)).allowEdit(element.enableExemptCode());
    }
}

public void modified()
{
    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    super();

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.enableButtonsActive_IN();
        TaxItemGroupHeading_ds.reread();
        TaxItemGroupHeading_ds.refresh();
    }
    // </GIN>

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        taxOnItem_ds.object(fieldnum(TaxOnItem, TaxExemptCode)).allowEdit(element.enableExemptCode());
        TaxOnItem_ds.rereadReferenceDataSources();
    }
    // </GBR>
}

void clicked()
{
    TaxRecalcForUnpostedTransaction taxRecalc = new TaxRecalcForUnpostedTransaction();
    taxRecalc.parmItemGroupHeadingDataSource(taxItemGroupHeading_ds);

    if (taxRecalc.prompt())
    {
        taxRecalc.runOperation();

        info("@SYS344901");
    }
}

void clicked()
{
    super();

    TaxRecalcForUnpostedTransaction taxRecalc = new TaxRecalcForUnpostedTransaction();
    taxRecalc.parmRecalcAll(true);

    if (taxRecalc.prompt())
    {
        taxRecalc.runOperation();
        info("@SYS344900");
    }
}

