void init()
{
    int hide = 2;

    super();

    taxTrans_ds.query().dataSourceTable(tablenum(TaxTrans)).addLink(fieldnum(CustInvoiceTrans,InventTransId), fieldnum(TaxTrans,InventTransId));
    taxTrans_ds.query().dataSourceTable(tablenum(TaxTrans)).addLink(fieldnum(CustInvoiceTrans,InvoiceDate), fieldnum(TaxTrans,TransDate));
    taxTrans_ds.query().dataSourceTable(tablenum(TaxTrans)).addRange(fieldnum(TaxTrans,InventTransId)).value(SysQuery::valueNotEmptyString());
    taxTrans_ds.query().dataSourceTable(tablenum(TaxTrans)).findRange(fieldnum(TaxTrans,InventTransId)).status(hide);
}
