[Form]
public class TaxTable extends FormRun implements TaxITableRefresh
{
    // <GCN>
    boolean       taxIntegrationSystem_CN;
    boolean       countryRegion_CN;
    // </GCN>
    // <GIN>
    boolean       countryRegion_IN;
    // </GIN>

    boolean             isVATEnabled;
    private Set allowedTypesForSourceManual;
    private Set allowedTypesForSourceTaxService;

}

public void enableFieldsActive_IN()
{
    // <GIN>
    boolean isMarked = TaxParameters::checkTaxParameters_IN();

    if (taxTable.TaxType_IN == TaxType_IN::None)
    {
        taxTable_ds.object(fieldNum(TaxTable, TaxComponentTable_IN)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxLedgerAccountGroup_IN)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxAccountGroup)).enabled(true);
    }
    else
    {
        taxTable_ds.object(fieldNum(TaxTable, TaxComponentTable_IN)).enabled(isMarked);
        taxTable_ds.object(fieldNum(TaxTable, TaxLedgerAccountGroup_IN)).enabled(isMarked);
        taxTable_ds.object(fieldNum(TaxTable, TaxAccountGroup)).enabled(false);
    }
    // </GIN>
}

public void init()
{
    // <GIN>
    #ISOCountryRegionCodes
    boolean isTaxesMarked  = TaxParameters::checkTaxParameters_IN();

    countryRegion_IN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    // </GIN>
    // <GCN>
    countryRegion_CN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN]);
    taxIntegrationSystem_CN = CustParameters::isTaxIntegrationSystem_CN();
    // </GCN>

    super();

    switch (element.args().dataset())
    {
        case tableNum(TaxJurisdiction):
            taxTable_ds.object(fieldNum(TaxTable, TaxJurisdictionCode)).visible(false);
            taxTable_ds.object(fieldNum(TaxTable, TaxJurisdictionCode)).allowEdit(false);
            taxTable_ds.object(fieldNum(TaxTable, TaxPeriod)).visible(false);
            taxTable_ds.object(fieldNum(TaxTable, TaxPeriod)).allowEdit(false);
            break;
    }

    // <GIN>
    if (countryRegion_IN)
    {
        taxType_IN.visible(isTaxesMarked);
        taxTable_ds.object(fieldNum(TaxTable, TaxType_IN)).visible(isTaxesMarked);
        taxTable_ds.object(fieldNum(TaxTable, TaxLedgerAccountGroup_IN)).visible(isTaxesMarked);
    }
    // </GIN>
    // <GCN>
    if (countryRegion_CN)
    {
        taxIntegration_CN.visible(taxIntegrationSystem_CN);
    }
    // </GCN>

    //<GTH>
    isVATEnabled = TaxThaiGovCertificationFeatureChecker::isAverageVATEnabled();

    if (isVATEnabled)
    {
        this.updateDesignForAverageVAT();
    }

    this.initTaxBaseTypeSet();

    //</GTH>

    boolean isRoundDeductibleFirstVisible = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]) && !TaxParameters::find().PurchTaxOnOperations;
    taxTable_ds.object(fieldNum(TaxTable, RoundDeductibleFirst)).visible(isRoundDeductibleFirstVisible);
    taxTable_ds.object(fieldNum(TaxTable, RoundDeductibleFirst)).allowEdit(isRoundDeductibleFirstVisible);
}

/// <summary>
/// Initializes the set of <c>TaxBaseType</c> for core tax and tax calculation service.
/// </summary>
protected void initTaxBaseTypeSet()
{
    allowedTypesForSourceManual = this.createTaxBaseTypeSet(TaxMasterDataSource::Manual);

    allowedTypesForSourceTaxService = this.createTaxBaseTypeSet(TaxMasterDataSource::TaxService);
}

private Set createTaxBaseTypeSet(TaxMasterDataSource _source)
{
    Set result = new Set(Types::Enum);

    result.add(TaxBaseType::PctPerNet);
    result.add(TaxBaseType::PctPerGross);
    result.add(TaxBaseType::PctPerTax);
    result.add(TaxBaseType::AmountByUnit);
    result.add(TaxBaseType::PctGrosOnNet);

    if (_source == TaxMasterDataSource::TaxService)
    {
        result.add(TaxBaseType::PctPerMargin);
    }

    return result;
}

private Set getTaxBaseTypeSet(TaxMasterDataSource _source)
{
    return _source == TaxMasterDataSource::Manual ? this.allowedTypesForSourceManual : this.allowedTypesForSourceTaxService;
}

void refreshTaxTable()
{
    ;
    taxTable_ds.reread();
    taxTable_ds.refreshEx();
}

public void resetTaxValues_IN()
{
    // <GIN>
    taxTable.TaxLedgerAccountGroup_IN = 0;
    taxTable.TaxComponentTable_IN     = 0;
    taxTable_ds.rereadReferenceDataSources();
    // </GIN>
}

void taxBaseEdit()
{
    FormDataObject taxOnTax = taxtable_ds.object(fieldNum(TaxTable, TaxOnTax));
    FormDataObject taxUnit = taxtable_ds.object(fieldNum(TaxTable, TaxUnit));
    FormDataObject taxPackagingTax = taxtable_ds.object(fieldNum(TaxTable, TaxPackagingTax));
    FormDataObject taxIncludeInTax = taxtable_ds.object(fieldNum(TaxTable, TaxIncludeInTax));
    FormDataObject reconcileAmountOrigin = taxtable_ds.object(fieldNum(TaxTable, ReconcileAmountOrigin));
    FormDataObject taxAllowLineDiscountOnTaxPerUnit = taxtable_ds.object(fieldNum(TaxTable, TaxAllowLineDiscountOnTaxPerUnit));

    taxOnTax.allowEdit(taxtable.isTaxOnTaxAllowed());
    taxOnTax.skip(!taxOnTax.allowEdit());

    taxUnit.allowEdit(taxtable.isTaxBaseByUnit() || taxtable.isTaxLimitBaseByUnit());
    taxUnit.skip(!taxUnit.allowEdit());

    taxPackagingTax.allowEdit(taxtable.isTaxBaseByUnit() || taxtable.isTaxLimitBaseByUnit());
    taxPackagingTax.skip(!taxPackagingTax.allowEdit());

    taxIncludeInTax.allowEdit(taxtable.isTaxBaseByUnit());
    taxIncludeInTax.skip(!taxIncludeInTax.allowEdit());

    reconcileAmountOrigin.allowEdit(taxtable.isReconcileAmountOriginAllowed());
    reconcileAmountOrigin.skip(!reconcileAmountOrigin.allowEdit());

    taxAllowLineDiscountOnTaxPerUnit.allowEdit(taxtable.TaxBase == TaxBaseType::PctPerGross);
    taxAllowLineDiscountOnTaxPerUnit.skip(!taxAllowLineDiscountOnTaxPerUnit.allowEdit());
}

void taxJurisdictionCodeEdit()
{
    if (taxTable.TaxJurisdictionCode &&
        taxTable.TaxPeriod == TaxJurisdiction::find(taxTable.TaxJurisdictionCode).TaxPeriod)
    {
        taxtable_ds.object(fieldNum(TaxTable,TaxPeriod)).allowEdit(false);
    }
    else
    {
        taxtable_ds.object(fieldNum(TaxTable,TaxPeriod)).allowEdit(true);
    }
}

display TaxReportLayout taxReportLayout()
{
    return TaxAuthorityAddress::find(TaxPeriodHead::find(taxTable.TaxPeriod).TaxAuthority).TaxReportLayout;
}

