    // </GTH>
}

private void setTaxTableEditable(TaxTable _taxTable)
{
    TaxRuntimeLookupTaxPeriodResult taxPeriodResult;
    TaxRuntimeLookupCondition lookupCondition;
    boolean isEditable = true;
    boolean isDeleteable = true;

    // The sales tax code map to tax setup should not be editable.
    if (!countryRegion_IN)
    {
        select firstonly RecId from taxPeriodResult
            where taxPeriodResult.TaxCode == _taxTable.TaxCode
                && _taxTable.TaxCode
            exists join lookupCondition
                where lookupCondition.RecId == taxPeriodResult.LookupCondition
                    && lookupCondition.Ledger == Ledger::current();

        if (taxPeriodResult.RecId)
        {
            isDeleteable = false;
        }
    }
    else
    {
        if (TaxTableRelationTable::findByTaxTable(_taxTable.RecId))
        {
            isEditable = false;
            isDeleteable = false;
        }
    }

    TaxTable_DS.allowEdit(isEditable);
    TaxTable_DS.allowDelete(isDeleteable);
}

/// <summary>
/// Updates form design for tax service.
/// </summary>
private void updateDesignForTaxService()
{
    boolean isTaxServiceEnabled = Tax::isTaxIntegrationEnabledForTaxService();

    TaxTable_ds.object(fieldNum(TaxTable, Source)).visible(isTaxServiceEnabled);
    TaxTable_ds.allowDelete(!(isTaxServiceEnabled && TaxTable.Source == TaxMasterDataSource::TaxService));

    this.initElementOfTaxBaseComboBoxControl();
}

private void initElementOfTaxBaseComboBoxControl()
{
    Set enumSet = this.getTaxBaseTypeSet(TaxTable.Source);
    SetEnumerator enumerator = enumSet.getEnumerator();

    TaxTable_TaxBase.clear();
    while (enumerator.moveNext())
    {
        TaxTable_TaxBase.add(enum2Str(enumerator.current()));
    }

    SysFormEnumComboBox taxBaseTypeControl = SysFormEnumComboBox::newParameters(
        element,
        element.controlId(formControlStr(TaxTable, TaxTable_TaxBase)),
        enumnum(TaxBaseType),
        enumSet
        );

    taxBaseTypeControl.select(enum2int(TaxTable.TaxBase));
}

public edit TaxationCode_BR editTaxationCode_BR(boolean set,
                                                TaxTable           _taxTable,
                                                TaxationCode_BR    _taxationCode)
{
    TaxationCode_BR taxationCode = _taxationCode;

    if (set)
    {
        _taxTable.TaxationCodeTable_BR = TaxationCodeTable_BR::find(_taxTable.TaxType_BR, taxationCode).RecId;
    }
    else
    {
        taxationCode = TaxationCodeTable_BR::findRecId(_taxTable.TaxationCodeTable_BR).TaxationCode;
    }

    return taxationCode;
}

void setFieldAccess_BR()
{
    taxTable_ds.object(fieldNum(TaxTable, RetainedTax_BR))         .enabled(true);
    taxTable_ds.object(fieldNum(TaxTable, IncludedTax_BR))         .enabled(true);
    taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR))     .enabled(true);
    taxTable_ds.object(fieldNum(TaxTable, TaxSubstitutionBaseRedCalcMode_BR)).enabled(false);

    if (taxTable.RetainedTax_BR == NoYes::Yes)
    {
        taxTable.IncludedTax_BR = NoYes::No;
        taxTable.TaxSubstitution_BR = TaxSubstitutionEnum_BR::None;
        taxTable_ds.object(fieldNum(TaxTable, IncludedTax_BR)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR)).enabled(false);
    }

    if (taxTable.IncludedTax_BR == NoYes::Yes)
    {
        taxTable.RetainedTax_BR = NoYes::No;
        taxTable.TaxSubstitution_BR = TaxSubstitutionEnum_BR::None;
        taxTable_ds.object(fieldNum(TaxTable, RetainedTax_BR)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR)).enabled(false);
    }

    if (taxTable.TaxType_BR == TaxType_BR::ICMSST)
    {
        taxTable.RetainedTax_BR = NoYes::No;
        taxTable.IncludedTax_BR = NoYes::No;
        taxTable_ds.object(fieldNum(TaxTable, RetainedTax_BR)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, IncludedTax_BR)).enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR)).enabled(true);
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitutionBaseRedCalcMode_BR)).enabled(true);
    }
    else
    {
        taxTable.TaxSubstitution_BR = TaxSubstitutionEnum_BR::None;
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR)).enabled(false);
    }

    if (taxTable.TaxType_BR == TaxType_BR::ICMSDiff)
    {
        taxTable.RetainedTax_BR = NoYes::No;
        taxTable_ds.object(fieldNum(TaxTable, RetainedTax_BR)).enabled(false);
    }

    if (taxTable.TaxType_BR == TaxType_BR::ImportTax)
    {
        taxTable.RetainedTax_BR = NoYes::No;
        taxTable.IncludedTax_BR = NoYes::No;
        taxTable.TaxSubstitution_BR = TaxSubstitutionEnum_BR::None;
        taxTable_ds.object(fieldNum(TaxTable, RetainedTax_BR))         .enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, IncludedTax_BR))         .enabled(false);
        taxTable_ds.object(fieldNum(TaxTable, TaxSubstitution_BR))     .enabled(false);
    }

    TaxTable_ds.object(fieldNum(TaxTable, ExcludeICMS_BR)).visible(
        (TaxTable.TaxType_BR == TaxType_BR::COFINS || TaxTable.TaxType_BR == TaxType_BR::PIS));

    TaxTable_ds.object(fieldNum(TaxTable, ExcludeISS_BR)).visible(
        (TaxTable.TaxType_BR == TaxType_BR::COFINS || TaxTable.TaxType_BR == TaxType_BR::PIS));
}

/// <summary>
/// Called whenever a record is saved (either new or updated).
/// If the tax code is new, then we create a blank
/// TaxData record for it.
/// </summary>
public void write()
{
    boolean isInsert = false;
    TaxData taxData;
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    ;
    if (!taxTable.RecId)
    {
        isInsert = true;
    }
    ttsbegin;
    super();
    if (isInsert
    // <GIN>
    && !(countryRegion_IN
             && (taxTable.TaxType_IN    == TaxType_IN::Excise
                 || taxTable.TaxType_IN == TaxType_IN::Customs))
    // </GIN>
    )
    {
        //<GEEU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
        {
            taxData.initValue();
        }
        //</GEEU>
        taxData.TaxCode = taxTable.TaxCode;
        taxData.insert();
    }
    ttscommit;
}

