public int active()
{
    int ret;

    ret = super();

    element.taxBaseEdit();
    element.taxJurisdictionCodeEdit();
    taxTable.enableDataSourceFields();

    // <GIN>
    if (countryRegion_IN)
    {
        element.enableFieldsActive_IN();
    }
    // </GIN>

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        taxTable_ds.setFieldAccess_BR();
    }
    // </GBR>

    //<GTH>
    if (isVATEnabled)
    {
        element.taxVATTypeEdit();
    }
    //</GTH>

    // <GTE>
    element.setTaxTableEditable(taxTable);
    // </GTE>

    element.updateDesignForTaxService();

    return ret;
}

