            public void modified()
            {
                super();

                if (BrazilParameters::isEnabled())
                {
					switch (TaxTable.TaxType_BR)
					{
						case TaxType_BR::ICMS :
						case TaxType_BR::PIS :
						case TaxType_BR::COFINS :
						case TaxType_BR::ISS :
							TaxTable.IncludedTax_BR = true;
                            TaxTable.RetainedTax_BR = false;
							break;

						case TaxType_BR::IPI :
						case TaxType_BR::ICMSST :
							TaxTable.IncludedTax_BR = false;
                            TaxTable.RetainedTax_BR = false;
							break;

						case TaxType_BR::IRRF :
						case TaxType_BR::INSSRetained :
                            TaxTable.IncludedTax_BR = false;
							TaxTable.RetainedTax_BR = true;
							break;
					}
                    taxTable_ds.setFieldAccess_BR();
                }
            }

