    AmountMST           eximBalanceTaxAmount;
    [FormObservable]
    AmountMST           eximTaxCorrectedAmount;
    [FormObservable]
    AmountMST           eximBalanceTaxCorrectedAmount;

    #ISOCountryRegionCodes
    boolean isRU;
    boolean isBR;
    boolean isIN;

    boolean isUnrealizedVATEnabled;
    boolean isTaxBranchEnabled;
    boolean isTaxTransExtensionTHEnabled;

}

public void enableFields_IN()
{
    boolean makeControlVisible;

    // The 'EximTaxTrans_IN' of data source is not depended on form menu, so we always show the 'customsAssessableValue_IN' display mehtod in India.
    makeControlVisible = fromMenu;
    customsAssessableValue_IN.visible(makeControlVisible);
    eximTaxTrans_IN_ds.object(fieldNum(EximTaxTrans_IN, AssessableValue)).visible(!makeControlVisible);
}

