    return eximTaxCorrectedAmount;
}

void init()
{
    isUnrealizedVATEnabled          = TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled();
    isTaxBranchEnabled              = TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled();
    isTaxTransExtensionTHEnabled    = TaxTransExtensionTH::isExtensionEnabled();

    isRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    isBR = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]);
    isIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);

    super();

    if (isIN)
    {
        TaxTransHistory_W_DS.object(fieldNum(TaxTransHistory_W, TaxComponentTable_IN)).visible(TaxParameters::checkTaxParameters_IN());
        TaxTransHistory_W_DS.object(fieldNum(TaxTransHistory_W, ServiceTaxReverseChargePercentage_IN)).visible(TaxParameters::isServiceTaxEnable_IN());
    }

    TaxTransHistory_W_DS.object(fieldNum(TaxTransHistory_W, VatDueDate_W)).visible(TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled());

    if (TaxParameters::find().PurchTaxOnOperations)
    {
        TaxTransHistory_DS.object(fieldNum(TaxTransHistory, VATExemptPct)).visible(false);
    }

    FormRun callerFormRun           = element.args().caller() as FormRun;
    str     callerFormName;
    str     taxWorkspaceFormName    = formStr(TaxWorkspace);

    if (callerFormRun)
    {
        callerFormName = callerFormRun.name();
    }

    if (element.args().caller() && (callerFormName != taxWorkspaceFormName || taxWorkspaceFormName == ''))
    {
        fromMenu = NoYes::No;
    }
    else
    {
        fromMenu = NoYes::Yes;
        totalAmountGroup.visible(false);
    }

    changeDisplaySign = true;

    if (fromMenu)
    {
        query = TaxTransHistory_DS.query();

        query.dataSourceTable(tableNum(TaxTransHistory)).addRange(fieldNum(TaxTransHistory,Voucher));
        query.dataSourceTable(tableNum(TaxTransHistory)).addRange(fieldNum(TaxTransHistory,TransDate));
        query.dataSourceTable(tableNum(TaxTransHistory)).addRange(fieldNum(TaxTransHistory,TaxCode));

        if (isTaxTransExtensionTHEnabled)
        {
            QueryBuildDataSource qbds;
            qbds = query.dataSourceTable(tableNum(TaxTransHistory)).addDataSource(tableNum(TaxTransHistory_TH));
            qbds.joinMode(JoinMode::InnerJoin);
            qbds.relations(false);
            qbds.addLink(fieldNum(TaxTransHistory, RecId), fieldNum(TaxTransHistory_TH, TaxTrans));

            if (isUnrealizedVATEnabled)
            {
                qbds.addRange(fieldNum(TaxTransHistory_TH, TaxInvoiceId));
                qbds.addRange(fieldNum(TaxTransHistory_TH, TaxInvoiceDate));
                qbds.addRange(fieldNum(TaxTransHistory_TH, TaxInvoiceReceiptDate));
            }
        }

        if (TaxEnableInquiryFormForPostedSalesTaxFeature::isEnabled())
        {
            element.promptVoucher();
        }
    }
    else
    {
        Common common = element.args().record();
        if (common is TaxArchiveTrans)
        {
            TaxArchiveTrans archiveTrans = common;
            ArchiveServiceJobDetailsView archiveServiceJobDetailsView;

            select firstonly ArchiveServiceJobId from archiveServiceJobDetailsView
                where archiveServiceJobDetailsView.SourceLinkTableId == tableNum(TaxArchiveTrans)
                    && archiveServiceJobDetailsView.SourceLinkRecId == archiveTrans.RecId;

            query = TaxTransHistory_DS.query();
            QueryBuildRange qbr = query.dataSourceTable(tableNum(TaxTransHistory)).addRange(fieldNum(TaxTransHistory, ArchiveServiceJobId));
            qbr.value(SysQuery::value(archiveServiceJobDetailsView.ArchiveServiceJobId));
        }
    }

    if (isIN)
    {
        customsCurrency_IN.visible(TaxParameters::find().Customs_IN);
        element.enableFields_IN();
    }

    if (isTaxBranchEnabled)
    {
        taxTrans_TaxBranch.visible(true);
        taxGroups_TaxBranch.visible(true);
    }

    if (ReverseChargeParameters_W::find().Enabled || TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxTransHistory_DS.object(fieldNum(TaxTransHistory, ReverseCharge_W)).visible(true);
    }

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        taxTrans_TaxAccountType.visible(true);
        totalAmountGroup.visible(false);
        taxTrans_SourceTaxAmountCur.visible(false);
        TaxGroups_TaxItemGroup.visible(false);
        TaxGroups_TaxGroup.visible(false);
    }

    if (TaxIntegrationUtils::isMultipleTaxIdEnabled())
    {
        TaxTransHistory_DS.object(fieldNum(TaxTransHistory, TaxID)).visible(true);
    }

    if (TaxIntegrationUtils::isMultipleTaxIdEnabledForTaxService())
    {
        TaxTransHistory_DS.object(fieldNum(TaxTransHistory, PartyTaxId)).visible(true);
        TaxRegistration_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());
    }
}

private void promptVoucher()
{
    QueryBuildDataSource qbds;

    queryRun = new QueryRun(query);
    if (!queryRun.prompt())
    {
        if (fromMenu)
        {
            element.close();
        }

    }

    if (isTaxTransExtensionTHEnabled)
    {
        qbds = queryRun.query().dataSourceTable(tableNum(TaxTransHistory_TH));
        if (!TaxThaiExtensionTableHelper::hasValidRange(qbds))
        {
            qbds.joinMode(JoinMode::OuterJoin);
        }
    }

    TaxTransHistory_DS.query(queryRun.query());
}

