private boolean doChangeDisplaySign(TaxTransHistory _taxTransHistory)
{
    boolean doChange = changeDisplaySign;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ]) && _taxTransHistory.IntracomVAT)
    {
        doChange = false;
    }

    return doChange;
}

public display TaxableTaxAmount_BR taxableTaxAmount_BR(TaxTransHistory _taxTransHistory)
{
    return  _taxTransHistory.taxTransHistory_BR().FiscalValue_BR == TaxFiscalValue_BR::WithCreditDebit ? _taxTransHistory.TaxAmount : 0;
}

public display TaxableBaseAmount_BR taxableBaseAmount_BR(TaxTransHistory _taxTransHistory)
{
    return _taxTransHistory.taxTransHistory_BR().FiscalValue_BR == TaxFiscalValue_BR::WithCreditDebit ? _taxTransHistory.TaxBaseAmount : 0;
}

public int active()
{
    int     ret;
    boolean enablePostingTab = false;

    #ISOCountryRegionCodes

    ret = super();

    if (MultipleTaxIdReportingHelper::isLegalEntityOrTaxCodeInCountryRegion(
        TaxTransHistory.TaxCode, [#isoIT], MultipleTaxIdScope::VATDeclaration))
    {
        enablePostingTab = true;
    }

    posting.visible(enablePostingTab);

    // <GIN>
    if (ret
        && isIN)
    {
        element.enableFields_IN();
    }
    // </GIN>

    return ret;
}

private int taxChangeDisplaySign(TaxDirection _taxDirection, TaxReverseCharge _taxReverseCharge = NoYes::No, boolean _changeSignForReverseCharge = true)
{
    if (changeDisplaySign && (_taxReverseCharge == NoYes::No || _changeSignForReverseCharge))
    {
        return Tax::changeDisplaySign(_taxDirection);
    }
    else
    {
        return 1;
    }
}

display AmountCur taxInCostPrice(TaxTrans _taxTrans)
{
    if (TaxParameters::find().PurchTaxOnOperations)
    {
        // If using US tax rules, tax in cost price should always be zero
        return 0;
    }
    if (_taxTrans.TaxInCostPriceRegulated)
        return _taxTrans.TaxInCostPriceRegulated;
    return _taxTrans.TaxInCostPrice;
}

void init()
{

    super();

    if (element.args().caller())
    {
        // <GEEU>
        if (!TaxParameters::reportUseVatDueDate_W())
        {
            // </GEEU>
            dateCriteria = this.query().dataSourceTable(tableNum(TaxTransHistory)).addRange(fieldNum(TaxTransHistory, TransDate));
            // <GEEU>
        }
        // </GEEU>
    }
    this.cacheAddMethod(identifierStr(displaySourceTaxAmountCur));
    this.cacheAddMethod(identifierStr(displayTaxInCostPrice));
    this.cacheAddMethod(identifierStr(displaySourceRegulateAmountCur));
    this.cacheAddMethod(identifierStr(displaySourceBaseAmountCur));

    // <GEEHU>
    TaxTransHistory_DS.cacheAddMethod(tableMethodStr(TaxTransHistory, settleDate));
    TaxTransHistory_DS.cacheAddMethod(tableMethodStr(TaxTransHistory, reportingDate));
    // </GEEHU>
}

void executeQuery()
{
    super();
}

