display TaxBaseCur displayAssessableValue_IN(TaxTransHistory _taxTransHistory)
{
    CustomsAmount_IN amount;
    TransDate        taxDate;

    if (TaxTable::find(_taxTransHistory.TaxCode).TaxType_IN == TaxType_IN::Customs)
    {
        taxDate = CustomsBillOfEntryNumberTable_IN::find(TaxTrans_IN::findRefRecId(_taxTransHistory.RecId).CustomsBillOfEntryNumberTable).BillOfEntryDate;
        if (!taxDate)
        {
            taxDate = _taxTransHistory.TransDate;
        }
        amount = CurrencyHelper_IN::customsMSTAmount_IN(_taxTransHistory.SourceBaseAmountCur, _taxTransHistory.SourceCurrencyCode, Direction_IN::Import, taxDate);
        amount = amount * this.taxChangeDisplaySign(_taxTransHistory.TaxDirection);
    }

    return amount;
}

display TaxAmountCur displayCustomsTaxAmountCur_IN(TaxTransHistory _taxTransHistory)
{
    return _taxTransHistory.taxTransHistory_W().CustomsDuty_IN * this.taxChangeDisplaySign(_taxTransHistory.TaxDirection);
}

