}

public void jumpRef()
{
    FormRun                 formRun;
    Args                    args;

    MenuFunction            menuFunction;

    args = new Args();
    args.caller(element);
    args.record(TaxTransHistory_DS.cursor());

    menuFunction = new MenuFunction(menuitemDisplayStr(LedgerTransVoucher1), MenuItemType::Display);

    formRun = menuFunction.create(args);
    formRun.run();
}

public boolean validateWrite()
{
    boolean ret = true;

    if (TaxTransHistory_TH.TaxTrans == 0)
    {
        TaxTransHistory_TH.TaxTrans = taxTransHistory.RecId;
    }

    ret = super();

    return ret;
}

public void init()
{
    if (isTaxTransExtensionTHEnabled)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void init()
{
    if (isBR)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}

public void init()
{
    super();

    if (element.args().caller())
    {
        if (TaxParameters::reportUseVatDueDate_W())
        {
            this.query().dataSourceTable(tableNum(TaxTransHistory_W)).joinMode(JoinMode::InnerJoin);
            dateCriteria = this.query().dataSourceTable(tableNum(TaxTransHistory_W)).addRange(fieldNum(TaxTransHistory_W, VatDueDate_W));
        }
    }
}

public void init()
{
    if (isRU)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}
