public void activate(boolean _active)
{
    // <GIN>
    boolean isWHTEnabled = TaxWithholdParameters_IN::checkTaxParameters();
    // </GIN>
    super(_active);

    // <GIN>
    taxWithholdTable_ds.object(fieldNum(TaxWithholdTable, ApplyThreshold_IN)).enabled(isWHTEnabled
        && taxWithholdTable.TaxType_IN != TaxWithholdCodeType_IN::None
        && !(TaxValueThresholdProfile_IN::existsBySource(TaxWithholdTable.TableId, TaxWithholdTable.RecId)));
    // </GIN>
}

public void enableFieldActive_IN()
{
    boolean isMarked     = TaxWithholdParameters_IN::checkTaxParameters();

    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdSettlementPeriod_IN)).enabled(isMarked && (taxWithholdTable.TaxType_IN != TaxWithholdCodeType_IN::None));
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, LedgerDimension_IN)).enabled( isMarked && (taxWithholdTable.TaxType_IN != TaxWithholdCodeType_IN::None));
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdComponent_IN)).enabled((taxWithholdTable.TaxType_IN != TaxWithholdCodeType_IN::None));

    if (taxWithholdTable.TaxType_IN == TaxWithholdCodeType_IN::None)
    {
        taxWithholdTable.TaxWithholdSettlementPeriod_IN                 = 0;
        taxWithholdTable.TaxWithholdComponent_IN                        = 0;
        taxWithholdTable.LedgerDimension_IN                             = 0;
    }
}

public void enableFieldVisible_BR()
{
    boolean isPISCOFINS = TaxWithholdTable.TaxWithholdType_BR == TaxWithholdType_BR::COFINS
        || TaxWithholdTable.TaxWithholdType_BR == TaxWithholdType_BR::PIS;

    TaxWithholdTable_ds.object(fieldNum(TaxWithholdTable, ExcludeICMS_BR)).visible(isPISCOFINS);

    TaxWithholdTable_ds.object(fieldNum(TaxWithholdTable, ExcludeISS_BR)).visible(isPISCOFINS);
}

public void init()
{
    // <GIN>
    boolean showIndia    = TaxWithholdParameters_IN::checkTaxParameters();
    // </GIN>

    super();
    TaxWithholdTable_TaxWithholdAccount.parmPostingType(LedgerPostingType::None);
    Reference_TaxWithholdAccount.parmPostingType(LedgerPostingType::None);

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        TaxWithholdTable_LedgerDimension_IN1.parmPostingType(LedgerPostingType::TaxWithhold);
        TaxWithholdTable_LedgerDimension_IN.parmPostingType(LedgerPostingType::TaxWithhold);
        TaxWithholdTable_InterimAccount_IN.parmPostingType(LedgerPostingType::TaxWithhold);

        taxType_IN.visible(showIndia);
        taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxType_IN)).visible(showIndia);
        taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdSettlementPeriod_IN)).visible(showIndia);
        taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, LedgerDimension_IN)).visible(showIndia);
        TaxWithholdTable_ds.object(fieldNum(TaxWithholdTable, InterimAccount_IN)).visible(showIndia);
    }
    // </GIN>

    if (BrazilParameters::isEnabled())
    {
        Reference_TaxWithholdAccountIncoming_BR.parmPostingType(LedgerPostingType::TaxWithhold);

        Reference_TaxReportLedgerDimension.parmPostingType(LedgerPostingType::TaxWithhold);
    }

    FormMenuButtonControl includedInWHTItemGroup = this.design().controlName(formControlStr(TaxWithholdTable, TaxWithholdItemGroup)) as FormMenuButtonControl;
    if (includedInWHTItemGroup)
    {
        includedInWHTItemGroup.visible(TaxWithholdingGlobalFeature::isExtendedWHTSupportedInCountryRegionOrParamEnabled());
    }
}

public void init()
{
    super();

    boolean enableWHT = TaxWithhold::enableAdvancedTaxWithhold();

    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdLedgerAccountGroup_TH)).mandatory(enableWHT);
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdPeriodHead_TH)).mandatory(enableWHT);
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, LedgerDimension)).mandatory(!enableWHT);

    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdLedgerAccountGroup_TH)).visible(enableWHT);
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, TaxWithholdPeriodHead_TH)).visible(enableWHT);
    taxWithholdTable_ds.object(fieldnum(TaxWithholdTable, LedgerDimension)).visible(!enableWHT);
}

public boolean validateWrite()
{
    boolean ret = true;

    if (TaxWithholdingGlobalFeature::isExtendedWHTSupportedInCountryRegionOrParamEnabled())
    {
        if (!taxWithholdTable.TaxWithholdLedgerAccountGroup_TH)
        {
            ret = checkFailed(strfmt("@SYS84753", fieldpname(TaxWithholdTable, TaxWithholdLedgerAccountGroup_TH)));
        }

        if (!taxWithholdTable.TaxWithholdPeriodHead_TH)
        {
            ret = checkFailed(strfmt("@SYS84753", fieldpname(TaxWithholdTable, TaxWithholdPeriodHead_TH)));
        }

        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoSA]) && taxWithholdTable.TaxWithholdBaseType == TaxWithholdBaseType::PctPerNet)
        {
            ret = checkFailed(strfmt("@TAX:WithholdingTaxCodeOriginSA", enum2str(TaxWithholdBaseType::PctPerNet), "@SYS306252"));
        }
    }
    else
    {
        if (!taxWithholdTable.LedgerDimension)
        {
            ret = checkFailed(strfmt("@SYS84753", fieldpname(TaxWithholdTable, LedgerDimension)));
        }
    }
    if (ret)
    {
        ret = super();
    }
    return ret;
}

public int active()
{
    int ret;

    ret = super();
    element.enableFieldVisible_BR();
    element.enableFieldActive_IN();
    taxWithholdLimit.enabled(taxWithholdTable.TaxType_IN    == TaxWithholdCodeType_IN::None);
    TaxValueThresholdProfile_IN.enabled(TaxWithholdTable.ApplyThreshold_IN);
    this.object(fieldNum(TaxWithholdTable, ApplyThreshold_IN)).enabled(!TaxValueThresholdProfile_IN::existsBySource(TaxWithholdTable.TableId, TaxWithholdTable.RecId));
    taxWithholdTable_ds.object(fieldNum(TaxWithholdTable, PanBasedAccumulation_IN)).enabled(taxWithholdTable.ApplyThreshold_IN);
    this.object(fieldNum(TaxWithholdTable, InterimAccount_IN)).visible(taxWithholdTable.TaxType_IN == TaxWithholdCodeType_IN::TCS);
    return ret;
}

public void modified()
{
    super();
    TaxValueThresholdProfile_IN.enabled(TaxWithholdTable.ApplyThreshold_IN);
    taxWithholdTable_ds.object(fieldNum(TaxWithholdTable, PanBasedAccumulation_IN)).enabled(taxWithholdTable.ApplyThreshold_IN);
    taxWithholdTable.PanBasedAccumulation_IN = NoYes::No;
}

public void modified()
{
    super();
    element.enableFieldActive_IN();
    taxWithholdLimit.enabled(taxWithholdTable.TaxType_IN    == TaxWithholdCodeType_IN::None);

    if (taxWithholdTable.TaxType_IN    != TaxWithholdCodeType_IN::None)
    {
        taxWithholdTable.TaxWithholdBaseType             = TaxWithholdBaseType::PctPerGross;
        taxWithholdTable.TaxWithholdSettlementPeriod_IN  = 0;
        taxWithholdTable.TaxWithholdComponent_IN    = 0;
    }
}

public void jumpRef()
{
    TaxWithholdType_IN  taxWithholdType;

    if (taxWithholdTable.TaxType_IN == TaxWithholdCodeType_IN::TCS)
    {
        taxWithholdType = TaxWithholdType_IN::TCS;
    }
    else
    {
        taxWithholdType = TaxWithholdType_IN::TDS;
    }
    TaxWithholdComponentTable_IN::jumpRef(TaxWithholdComponentTable_IN::find(taxWithholdTable.TaxWithholdComponent_IN).Component, taxWithholdType);
}

public void jumpRef()
{
    TaxWithholdType_IN  taxWithholdType;

    if (taxWithholdTable.TaxType_IN == TaxWithholdCodeType_IN::TCS)
    {
        taxWithholdType = TaxWithholdType_IN::TCS;
    }
    else
    {
        taxWithholdType = TaxWithholdType_IN::TDS;
    }

    TaxWithholdHead_IN::jumpRef(TaxWithholdHead_IN::find(taxWithholdTable.TaxWithholdSettlementPeriod_IN).Period, taxWithholdType);
}

public void lookup()
{
    TaxWithholdCodeType_IN      taxWithholdCodeType_IN;

    TaxWithholdComponentTable_IN::lookupComponentByTaxCode_IN(this, str2enum(taxWithholdCodeType_IN, TaxType_IN_TaxType_IN.valueStr()));
}
