[Form]
public class TaxWithholdTrans extends FormRun
{
    [FormObservable]
    TaxWithholdAmountCur  taxWithholdAmountTotal;
    NoYes                 fromMenu;
    Query                 query;

    boolean               taxWithholdTransExtensionTHEnabled;

}

public void init()
{
    QueryBuildRange queryBuildRange;

    // <GIN>
    QueryBuildDataSource    comGroupTable;
    QueryBuildDataSource    comTable;
    // </GIN>

    //<GTH>
    QueryBuildDataSource    qbds;
    Name                    callerName;
    TaxWithholdReportPeriod_TH  taxWithholdReportPeriod;

    taxWithholdTransExtensionTHEnabled = TaxWithholdTransExtensionTH::isExtensionEnabled();
    //</GTH>
    #ISOCountryRegionCodes

    super();

    //<GTH>
    if (TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled())
    {
        element.initTaxWithholdControls_TH();
    }
    if (TaxThaiGovCertificationFeatureChecker::isTaxBranchEnabled())
    {
        displayTaxBranchDimension.visible(true);
        TaxWithholdGroup_M_displayTaxBranchDimension.visible(true);
    }
    //</GTH>

    FormRun caller = element.args().caller();

    if (caller)
    {
        fromMenu    = NoYes::No;
        callerName  = element.args().caller().name();
    }
    else
    {
        fromMenu    = NoYes::Yes;
        totalAmountGroup.visible(false);
    }

    if (fromMenu)
    {
        query   = new Query();

        query.addDataSource(tableNum(TaxWithholdTrans));
        query.dataSourceNo(1).addRange(fieldNum(TaxWithholdTrans,Voucher));
        query.dataSourceNo(1).addRange(fieldNum(TaxWithholdTrans,TransDate));
        query.dataSourceNo(1).addRange(fieldNum(TaxWithholdTrans,TaxWithholdCode));

        queryBuildRange = query.dataSourceNo(1).addRange(fieldNum(TaxWithholdTrans,Posted));
        queryBuildRange.value(queryValue(NoYes::Yes));
        queryBuildRange.status(RangeStatus::Locked);

        // <GIN>
        if (TaxWithholdParameters_IN::checkTaxParameters())
        {
            query.dataSourceNo(1).addRange(fieldnum(TaxWithholdTrans, TaxType_IN));
            comGroupTable = query.dataSourceNo(1).addDataSource(tableNum(TaxWithholdComponentGroupTable_IN));
            comGroupTable.addLink(fieldNum(TaxWithholdTrans, TaxWithholdComponentGroupTable_IN), fieldNum(TaxWithholdComponentGroupTable_IN, RecId));
            comGroupTable.addRange(fieldnum(TaxWithholdComponentGroupTable_IN, ComponentGroup));
            comTable = query.dataSourceNo(1).addDataSource(tableNum(TaxWithholdComponentTable_IN));
            comTable.addLink(fieldNum(TaxWithholdTrans, TaxWithholdComponentTable_IN), fieldNum(TaxWithholdComponentTable_IN, RecId));
            comTable.addRange(fieldnum(TaxWithholdComponentTable_IN, Component));
        }
        // </GIN>

        //<GTH>
        if (taxWithholdTransExtensionTHEnabled)
        {
            qbds = query.dataSourceTable(tableNum(TaxWithholdTrans)).addDataSource(tableNum(TaxWithholdTransExtensionTH));
            qbds.joinMode(JoinMode::OuterJoin);
            qbds.relations(true);
        }
        //</GTH>

        taxWithholdTrans_ds.query(query);
    }
    else if (BrazilParameters::isEnabled() && callerName == formStr(TaxWithholdReportVoucher) && element.args().dataset() == tablenum(TaxWithholdReportVoucher))
    {
        TaxWithholdReportVoucher taxWithholdReportVoucher = element.args().record();

        // Show posted transactions related with the interval withholding tax settlement
        if (taxWithholdReportVoucher)
        {
            query = taxWithholdTrans_ds.query();

            query.dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, TaxWithholdPeriod_BR));
            query.dataSourceTable(tableNum(TaxWithholdTrans)).rangeField(fieldNum(TaxWithholdTrans, TaxWithholdPeriod_BR)).value(strFmt('%1', taxWithholdReportVoucher.TaxWithholdPeriod));

            query.dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, TransDate));
            query.dataSourceTable(tableNum(TaxWithholdTrans)).rangeField(fieldNum(TaxWithholdTrans, TransDate)).value(queryRange(taxWithholdReportVoucher.FromDate, taxWithholdReportVoucher.ToDate));

            query.dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, Posted));
            query.dataSourceTable(tableNum(TaxWithholdTrans)).rangeField(fieldNum(TaxWithholdTrans, Posted)).value(strFmt('%1', 1));

            query.dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, CanBeReversed_BR));
            query.dataSourceTable(tableNum(TaxWithholdTrans)).rangeField(fieldNum(TaxWithholdTrans, CanBeReversed_BR)).value(strFmt('%1', 1));

            query.dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, TaxRepCounter_BR));
            query.dataSourceTable(tableNum(TaxWithholdTrans)).rangeField(fieldNum(TaxWithholdTrans, TaxRepCounter_BR)).value(strFmt('%1', taxWithholdReportVoucher.TaxRepCounter));
        }
    }

    if (TaxWithhold::enableAdvancedTaxWithhold() &&
        !fromMenu                                &&
        (callerName == formStr(TaxWithholdReportVoucher_TH) ||
         callerName == formStr(TaxWithholdPeriod_TH)))
    {
        taxWithholdTrans_ds.query().dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, Posted)).value(queryValue(NoYes::Yes));

        if (callerName == formStr(TaxWithholdPeriod_TH))
        {
            taxWithholdReportPeriod = element.args().record();
            taxWithholdTrans_ds.query().dataSourceTable(tableNum(TaxWithholdTrans)).addRange(fieldNum(TaxWithholdTrans, TransDate)).value(queryRange(taxWithholdReportPeriod.TaxWithholdFromDate, taxWithholdReportPeriod.TaxWithholdToDate));
        }
    }
}

/// <summary>
/// Initiate Thailand withholding tax controls.
/// </summary>
public void initTaxWithholdControls_TH()
{
    // <GTH>
    // Show TH fields
    TaxWithholdTransExtensionTH_ds.object(fieldNum(TaxWithholdTransExtensionTH, ReceiptNumber)).visible(true);
    TaxWithholdTransExtensionTH_ds.object(fieldNum(TaxWithholdTransExtensionTH, HideInReports)).visible(true);

    if (element.args().menuItemName() == menuitemDisplayStr(TaxWithholdTransEditing_TH))
    {
        // Posted withholding tax editing
        element.design().caption("@SYS4082497");

        TaxWithholdTrans_ds.allowEdit(true);

        // Show Edit/Save button
        element.design().viewEditMode(ViewEditMode::Auto);
    }
    // </GTH>
}

public void write()
{
    if (TaxWithholdTrans.RecId)
    {
        TaxWithholdTransExtensionTH.TaxWithholdTrans = TaxWithholdTrans.RecId;
        super();
    }
}

public void init()
{
    if (taxWithholdTransExtensionTHEnabled)
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();
}
