public void clearDataSource()
{
    ttsbegin;
    delete_from tmpTaxWithholdCertificateHeader_IN;
    delete_from tmpTaxWithholdCertificateLine_IN;
    ttscommit;
}

public void clearText()
{
    certificateNumber.text("");
    accountType.text("");
    account.text("");
    name.text("");
    fromDate.dateValue(dateNull());
    toDate.dateValue(dateNull());
    certificatedate.dateValue(dateNull());
    element.enableDisableButton(true,false);
}

public void enableDisableButton(boolean _true, boolean _false)
{
    include.enabled(_true);
    includeall.enabled(_true);
    exclude.enabled(_false);
    excludeall.enabled(_false);
}

public void init()
{
    #Operator_IN

    super();
    element.enableDisableButton(true,false);
}

public void initFromTaxWithholdCertificate()
{
    TaxWithholdCertificate_IN    taxWithholdCertificate;
    taxWithholdCertificate = TaxWithholdCertificate_IN::findtaxWithholdCertificate_IN(certificateNumber.valueStr(), taxType.selection());

    if (taxWithholdCertificate)
    {
        custVendACType = taxWithholdCertificate.CustVendACType;
        certificatedate.dateValue(taxWithholdCertificate.TransDate);
        accountType.text(enum2str(taxWithholdCertificate.CustVendACType));

        account.text(LedgerDimensionFacade::getDisplayValueForLedgerDimension(taxWithholdCertificate.LedgerDimension));
        name.text(taxWithholdCertificate.Name);
    }
    else
    {
        checkFailed(strfmt("@GLS6137",certificateNumber.valueStr(), taxType.valueStr()));
        element.clearText();
    }
}

public void showData()
{
    if (!fromdate.dateValue() || !toDate.dateValue())
    {
        checkFailed("@SYS22087");
    }
    else
    {
        element.clearDataSource();
        tmpTaxWithholdCertificateHeader_IN.initFromTaxWithholdTrans(taxType.selection(),
                                                                   account.valueStr(),
                                                                   fromDate.dateValue() ,
                                                                   toDate.dateValue(),
                                                                   certificateNumber.valueStr(),
                                                                   tmpTaxWithholdCertificateHeader_IN,
                                                                   custVendACType);
        if (tmpTaxWithholdCertificateHeader_IN)
        {
            element.enableDisableButton(true,false);
        }
        tmpTaxWithholdCertificateHeader_IN_DS.executeQuery();
    }
}

