public void clicked()
{
    TaxWithholdCertificateTrans_IN   taxWithholdCertificateTrans;

    super();
    if (tmpTaxWithholdCertificateLine_IN)
    {
        taxWithholdCertificateTrans.initFromCertificateLine(certificateNumber.text(),
                                                           certificatedate.dateValue(),
                                                           custVendACType, account.text(),
                                                           tmpTaxWithholdCertificateLine_IN);
        tmpTaxWithholdCertificateLine_IN_DS.executeQuery();
    }
}

