public void init()
{
    super();

    if (!element.args().caller())
    {
        throw error("@SYS22996");
    }

    switch (element.args().record().TableId)
    {
        case (tablenum(TaxYearlyCom_IT)):
            {
                QueryBuildDataSource qbds = taxReportHeader_IT_DS.query().dataSourceTable(tableNum(TaxYearlyComReport_IT));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReport_IT, Year),
                    element.args().record(),
                    fieldNum(TaxYearlyCom_IT, Year));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReport_IT, ATECOFINCode),
                    element.args().record(),
                    fieldNum(TaxYearlyCom_IT, ATECOFINCode));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReport_IT, TaxYearlyComId),
                    element.args().record(),
                    fieldNum(TaxYearlyCom_IT, TaxYearlyComId));
                original = NoYes::No;
            }
            break;
        case (tablenum(TaxYearlyComV2_IT)):
            {
                QueryBuildDataSource qbds = taxReportHeader_IT_DS.query().dataSourceTable(tableNum(TaxYearlyComReportV2_IT));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReportV2_IT, Year),
                    element.args().record(),
                    fieldNum(TaxYearlyComV2_IT, Year));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReportV2_IT, ATECOFINCode),
                    element.args().record(),
                    fieldNum(TaxYearlyComV2_IT, ATECOFINCode));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReportV2_IT, TaxYearlyComId),
                    element.args().record(),
                    fieldNum(TaxYearlyComV2_IT, TaxYearlyComId));
                qbds.addDynalink(
                    fieldNum(TaxYearlyComReportV2_IT, TaxPeriod),
                    element.args().record(),
                    fieldNum(TaxYearlyComV2_IT, TaxPeriod));
                original = NoYes::No;
            }
            break;

        case (tablenum(TaxYearlyComReport_IT)):
            original = NoYes::Yes;
            taxYearlyComReport_IT_ds.allowEdit(false);
            break;

        case (tablenum(TaxYearlyComReportV2_IT)):
            original = NoYes::Yes;
            taxYearlyComReportV2_IT_ds.allowEdit(false);
            break;

        default:
            throw error("@SYS22996");
    }

    taxReportHeader_IT_DS.query().dataSourceTable(tableNum(TaxYearlyComReport_IT)).enabled(!taxYearlyCommunicationEnhancementFeature);
    taxReportHeader_IT_DS.query().dataSourceTable(tableNum(TaxYearlyComReportV2_IT)).enabled(taxYearlyCommunicationEnhancementFeature);
    TaxReportHeader_IT_DS.executeQuery();

    HeaderGroup.visible(!taxYearlyCommunicationEnhancementFeature);
    HeaderGroupV2.visible(taxYearlyCommunicationEnhancementFeature);
    CompanyInformation.visible(!taxYearlyCommunicationEnhancementFeature);
    CompanyInformationV2.visible(taxYearlyCommunicationEnhancementFeature);
    Writer_WriterFiscalCode.visible(!taxYearlyCommunicationEnhancementFeature);
    Writer_WriterRole.visible(!taxYearlyCommunicationEnhancementFeature);
    Writer_WriterFiscalCodeV2.visible(taxYearlyCommunicationEnhancementFeature);
    Writer_WriterRoleV2.visible(taxYearlyCommunicationEnhancementFeature);
}

public void setAllowEditWriter()
{
    boolean allowEdit = !taxReportHeader_IT.WriterIsLegalEntity;

    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterLastName)).allowEdit(allowEdit);
    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterFirstName)).allowEdit(allowEdit);
    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterGender)).allowEdit(allowEdit);
    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterBirthDate)).allowEdit(allowEdit);
    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterBirthPlace)).allowEdit(allowEdit);
    taxReportHeader_IT_DS.object(fieldNum(TaxReportHeader_IT, WriterBirthCounty)).allowEdit(allowEdit);
}

