[Form]
public class TransTaxInformation extends FormRun
{
    TransTaxInformationUIController     transTaxInformationUIController;
    List                                list = new List(Types::Class);
    Common                              trans;
    Common                              transHeadTable = null;

    #ISOCountryRegionCodes

    TaxModelTaxable                     taxModel;
    boolean                             cust_enable = false;
    boolean                             vend_enable = false;
    boolean                             item_enable = false;

}

public void init()
{
    trans = element.args().record();

    list transitDocumentTypeList = TransitDocumentManager_IN::newManager().findTransitDocTypes(trans.TableId, tableNum(TransTaxInformation));

    if (transitDocumentTypeList && transitDocumentTypeList.elements() > 1)
    {
        element.form().design().dialogSize(DialogSize::Large);
    }
    taxModel = TaxModelDocLineFactory::newTaxModelDocLine(trans);

    super();

    if (trans.RecId == 0)
    {
        throw error(strfmt("@SYS25516", element.name()));
    }

    this.initControls();
    transTaxInformationGrid.visible(transitDocumentTypeList.elements() > 1);
    this.modifyQueryRange();

    transTaxInformationUIController = TransTaxInformationUIController::construct(trans, TransTaxInformation, list);

    if(element.args().caller().inViewMode())
    {
        transTaxInformationUIController.parmFormOpenMode(OpenMode::View);
    }
    else
    {
        transTaxInformationUIController.parmFormOpenMode(OpenMode::Edit);
    }

    transTaxInformationUIController.run();
    transTaxInformation_DS.executeQuery();
}

