/// <summary>
/// Perform new range when show the tax information.
/// </summary>
private void modifyQueryRange()
{
    QueryBuildDataSource qbdsTransTaxInformationRelationView;

    qbdsTransTaxInformationRelationView = SysQuery::findOrCreateDataSource(transTaxInformation_DS.query(), tableNum(TransTaxInformationRelationView), tableNum(TransTaxInformation));
    qbdsTransTaxInformationRelationView.clearLinks();
    qbdsTransTaxInformationRelationView.joinMode(JoinMode::InnerJoin);
    qbdsTransTaxInformationRelationView.addLink(fieldNum(TransTaxInformation, RecId), fieldNum(TransTaxInformationRelationView, TransTaxInformationRefRecId));

    SysQuery::findOrCreateRange(qbdsTransTaxInformationRelationView, fieldNum(TransTaxInformationRelationView, TransactionRefRecId)).value(queryValue(trans.RecId));
    SysQuery::findOrCreateRange(qbdsTransTaxInformationRelationView, fieldNum(TransTaxInformationRelationView, TransactionRefTableId)).value(queryValue(trans.TableId));

    transTaxInformation_DS.executeQuery();
}

/// <summary>
/// Closes the form.
/// </summary>
public void close()
{
    super();

    FormRun callerFormObject = element.args().caller() as FormRun;
    if (callerFormObject.name() == formStr(LedgerJournalTransVendInvoice)
        || callerFormObject.name() == formStr(LedgerJournalTransInvoiceRegister)
        || callerFormObject.name() == formStr(LedgerJournalTransDaily))
    {
        callerFormObject.refresh();
    }
}

private void markCurrentTaxDocumentTaxStatusDirty()
{
    LedgerJournalTrans ledgerJournalTrans;
    LedgerJournalTable ledgerJournalTable;

    try
    {
        if (trans)
        {
            SalesPurchJournalLine journalLine = trans as SalesPurchJournalLine;
            transHeadTable = journalLine.SalesPurchJournalLine::salesPurchJournalTable();

            if (trans is LedgerJournalTrans)
            {
                ledgerJournalTrans = trans as ledgerJournalTrans;
                ledgerJournalTable = ledgerJournalTrans.ledgerJournalTable();
            }
        }
    }
    catch(Exception::Error)
    {
        transHeadTable = null;
    }

    if (transHeadTable)
    {
        TaxableDocumentDescriptor descriptor = TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(transHeadTable);
        if (descriptor)
        {
            ITaxableDocument taxableDocument = TaxableDocumentObject::construct(descriptor);
            if (!ledgerJournalTable || (ledgerJournalTable && ledgerJournalTable.SystemBlocked == NoYes::No))
            {
                TaxBusinessService::markTaxDocumentTaxStatus(taxableDocument, TaxDocumentCalculationLevel::Dirty);

                if (!taxableDocument.skipTaxDocument())
                {
                    TaxDocumentSourceDocumentIntegrator::updateDistribution(
                        taxableDocument.getTransactionTableId(),
                        taxableDocument.getTransactionRecordId());
                }
            }
        }
    }

    if (transHeadTable)
    {
        boolean isGTEEnabled = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();

        if (ledgerJournalTable)
        {
            if (ledgerJournalTable.SystemBlocked == NoYes::No)
            {
                if (!isGTEEnabled)
                {
                    TaxUncommitted::deleteForVoucher(ledgerJournalTable.TableId, ledgerJournalTable.RecId, ledgerJournalTrans.Voucher);
                }
                LedgerJournalTrans::getTaxInstance(ledgerJournalTable.JournalNum, ledgerJournalTrans.Voucher, ledgerJournalTrans.Invoice, true, null, false, ledgerJournalTrans.transdate);

                if (TaxWithholdParameters_IN::checkTaxParameters())
                {
                    TaxWithholdUncommitted_IN::deleteForVoucher(ledgerJournalTable.TableId, ledgerJournalTable.RecId, ledgerJournalTrans.Voucher);
                    LedgerJournalTrans_IN_Extension::getTaxWithholdInstance_IN(ledgerJournalTable.JournalNum, ledgerJournalTrans.Voucher, ledgerJournalTrans.Invoice, true);
                }
            }
        }
        else
        {
            if (!isGTEEnabled)
            {
                TaxUncommitted::deleteForDocumentHeader(transHeadTable.TableId, transHeadTable.RecId);
            }
            else if (transHeadTable is TaxEngineProjAdvancedJournalLineHeader)
            {
                ProjAdvancedJournalLine projAdvancedJournalLine = trans as ProjAdvancedJournalLine;
                if (projAdvancedJournalLine.ProvidedTaxAmount)
                {
                    // For project advanced journal, provided tax amount is calculated when it has value and tax adjustment will be created.
                    // So, here clean adjustment should be done together when tax document is marked dirty.
                    TaxDocumentRowMeasureAdjustment::cleanAdjustmentForHeader(transHeadTable.TableId, transHeadTable.RecId);
                }
            }

            if (TaxWithholdParameters_IN::checkTaxParameters())
            {
                TaxWithholdUncommitted_IN::deleteForDocumentHeader(transHeadTable.TableId, transHeadTable.RecId);
            }
        }
    } 
}

public void write()
{
    super();

    // <GTE>
    if (trans.TableId       != tableNum(ProjTable)
        && trans.TableId    != tableNum(ProdTable)
        && trans.TableId    != tableNum(ProjInvoiceTable)
        && trans.TableId    != tableNum(CustInvoiceLineTemplateTaxExtension_IN)
        && trans.TableId    != tableNum(InventJournalTrans)
        && trans.TableId    != tableNum(ProjJournalTrans)
        && trans.TableId    != tableNum(TmpProjAdjustment)
        && trans.TableId    != tableNum(TmpProjAdjustmentCreate)
        && trans.TableId    != tableNum(SMAServiceOrderLine)
        && trans.TableId    != tableNum(ProdJournalBOM)
        && trans.TableId    != tableNum(TSTimesheetLine)
        && TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        this.markCurrentTaxDocumentTaxStatusDirty();

        TaxRecalculateProjProposalInvoiceAmountContext taxRecalculateProjProposalInvoiceAmountContext = TaxRecalculateProjProposalInvoiceAmountContext::current();

        if (taxRecalculateProjProposalInvoiceAmountContext)
        {
            // When tax information changed, the invoice amount of project proposal journal needs to be recalculated.
            taxRecalculateProjProposalInvoiceAmountContext.parmShouldRecalculateInvoiceAmount(true);
        }
    }
    // </GTE>
}

public void modified()
{
    if (TransTaxInformation_ds.object(fieldNum(TransTaxInformation, ApplyGSTTCS)).getValue() == NoYes::Yes)
    {
        TransTaxInformation_ds.object(fieldNum(TransTaxInformation, ApplyGSTTDS)).setValue(NoYes::No);
    }
}

public void modified()
{
    if (TransTaxInformation_ds.object(fieldNum(TransTaxInformation, ApplyGSTTDS)).getValue() == NoYes::Yes)
    {
        TransTaxInformation_ds.object(fieldNum(TransTaxInformation, ApplyGSTTCS)).setValue(NoYes::No);
    }
}
