public class TransportationDocument extends FormRun
{
    #ISOCountryRegionCodes

    private boolean sourceAllowEdit;
    private Common sourceRecord;
    private FieldId transportationDocumentFieldId;
    private TransportationDocumentRecId transportationDocumentRecId;

}

public Common getPostalAddress(Description _loadedPostalName)
{
    Common ret;
    LoadedPostalAddress                 loadedPostalAddress;
    LogisticsPostalAddress              logisticsPostalAddress;
    LogisticsLocationSelectionLookup    logisticsLocationSelectionLookup;
    QueryRun                            logisticsPostalAddressQueryRun;

    if (_loadedPostalName)
    {
        // By default assume it does not have the correct source type.
        ret = null;

        // Build the location selection query.
        logisticsLocationSelectionLookup = LogisticsLocationSelectionLookup::construct(transportationDocument.getLoadedPostalAddressLookupRecord());
        logisticsLocationSelectionLookup.parmReturnLocation(false);
        logisticsLocationSelectionLookup.parmIncludeAllRoles(true);
        logisticsLocationSelectionLookup.buildQuery();

        logisticsPostalAddressQueryRun = new QueryRun(logisticsLocationSelectionLookup.parmQuery());

        while (logisticsPostalAddressQueryRun.next())
        {
            logisticsPostalAddress = logisticsPostalAddressQueryRun.get(tableNum(LogisticsPostalAddress));

            if (_loadedPostalName == LogisticsPostalAddress::locationNameFromRecId(logisticsPostalAddress.RecId))
            {
                // The source type selection contains the entered LoadedPostalAddress.
                ret = logisticsPostalAddress.data();
                break;
            }
        }
    }
    return ret;
}

public void init()
{
    sourceRecord = element.args().record();

    if (sourceRecord)
    {
        // Find the field named 'TransportationDocument' on the table of the record that was used to open the form.
        transportationDocumentFieldId = fieldName2id(sourceRecord.TableId, identifierStr(TransportationDocument));
    }

    if (!transportationDocumentFieldId)
    {
        throw error("@SYS22996");
    }

    super();

    // <GEELT>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoMX]))
    {
        element.setVisible();
        element.setEnvironmentalDamageInsuranceVisibility();
    }
    // </GEELT>

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        Vehicle_TrailerNumber.visible(false);
    }

    GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceEEU00019, funcName());
}

private void setEnvironmentalDamageInsuranceVisibility()
{
    InventTable inventTable;

    switch (sourceRecord.TableId)
    {
        case tableNum(InventTransferTable):
            InventTransferTable inventTransferTable = sourceRecord;
            InventTransferLine inventTransferLine;

            select ItemId from inventTransferLine
                where inventTransferLine.TransferId == inventTransferTable.TransferId
                join inventTable 
                    where inventTable.ItemId == inventTransferLine.ItemId && inventTable.HMIMIndicator == NoYes::Yes;
            break;

        case tableNum(SalesTable):
            SalesTable salesTable = sourceRecord;
            SalesLine salesLine;

            select ItemId from salesLine
                where salesLine.SalesId == salesTable.SalesId
                join inventTable
                    where inventTable.ItemId == salesLine.ItemId && inventTable.HMIMIndicator == NoYes::Yes;
            break;

        case tableNum(CustPackingSlipJour):
            CustPackingSlipJour custPackingSlipJour = sourceRecord;
            SalesLine slipSalesLine;

            select ItemId from slipSalesLine
                where slipSalesLine.SalesId == custPackingSlipJour.SalesId
                join inventTable
                    where inventTable.ItemId == slipSalesLine.ItemId && inventTable.HMIMIndicator == NoYes::Yes;

            break;
    }

    EnvironmentalDamageInsurance_MX.visible(inventTable != null);
}

public void setAllowEdit(boolean _allowEdit)
{
    transportationDocument_ds.allowEdit(_allowEdit);
    transportationVehicle_ds.allowEdit(_allowEdit);

    loadingPostalAddressSelect.enabled(_allowEdit);
    loadingPostalAddressNew.enabled(_allowEdit);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        UnloadingPostalAddressSelect.enabled(_allowEdit);
        UnloadingPostalAddressCompanyNew.enabled(_allowEdit);
    }
}

public void setAllowEditCarrierCode()
{
    FormDataObject carrierCodeField;

    carrierCodeField = transportationDocument_ds.object(fieldNum(TransportationDocument, CarrierCode));

    // The CarrierCode field is only enabled if the CarrierType is not InvoiceAccount.
    carrierCodeField.allowEdit(transportationDocument.CarrierType != CarrierType_W::InvoiceAccount);
}

/// <summary>
/// Set visible properties for controls
/// </summary>
private void setVisible()
{
    switch (sourceRecord.TableId)
    {
        case tableNum(LtAssetPackingSlip):
            UnloadedPage.visible(true);
            transportationDocument_DS.object(fieldNum(TransportationDocument, DocumentDateTime)).visible(false);
            break;

        case tableNum(InventTransferTable):
            transportationDocument_DS.object(fieldNum(TransportationDocument, DocumentDateTime)).visible(true);

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
            {
                LoadedPage.visible(true);
                UnloadedPage.visible(true);
                transportationDocument_DS.object(fieldNum(TransportationDocument, UnloadedDateTime)).visible(false);
            }
            else
            {
                LoadedPage.visible(false);
            }
            break;

        default:
            transportationDocument_DS.object(fieldNum(TransportationDocument, DocumentDateTime)).visible(false);
            break;
    }
}

public void close()
{
    super();

    FormRun callerForm = element.args().caller() as FormRun;

    if (callerForm 
        && formHasMethod(callerForm, identifierStr(transportationDocumentFormClosed)))
    {
        callerForm.transportationDocumentFormClosed();
    }
}

