    transportationDocument_ds.refresh();
}

public int active()
{
    int isValid;

    isValid = super();

    if (FormDataUtil::isFormDataSource(sourceRecord))
    {
        FormDataSource sourceDS = FormDataUtil::getFormDataSource(sourceRecord);
        switch (sourceRecord.TableId)
        {
            case tableNum(LtAssetPackingSlip):
                sourceAllowEdit = true;
                break;

            case tableNum(SalesTable):
                if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLV, #isoLT]))
                {
                    SalesTable salesTable = sourceRecord;
                    sourceAllowEdit = salesTable.SalesType == SalesType::ReturnItem || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]);
                }
                else
                {
                    sourceAllowEdit = sourceDS.allowEdit();
                }
                break;

            case tableNum(PurchTable):
                if (!SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLV, #isoLT]))
                {
                    PurchTable purchTable = sourceRecord;
                    sourceAllowEdit = purchTable.PurchaseType != PurchaseType::ReturnItem || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]);
                }
                else
                {
                    sourceAllowEdit = sourceDS.allowEdit();
                }
                break;

            default:
                sourceAllowEdit = sourceDS.allowEdit();
        }
    }
    else
    {
        // Prevent editing when called with a source record that is not a form data source.
        // This ensures that no updates will be made to the calling record that may not get committed.
        sourceAllowEdit = false;
    }

    // Set the form title text to the Title fields of the passed in record.
    sourceTitle.text(this.titleFields(sourceRecord));

    transportationDocumentRecId = sourceRecord.(transportationDocumentFieldId);

    // Only allow a record to be created if one doesn't already exist and the passed in record's datasource allowed editing.
    transportationDocument_ds.allowCreate((transportationDocument.RecId == 0) && sourceAllowEdit);

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]))
    {
        EInvoiceTransportationActors_MX.openMode(sourceAllowEdit ? OpenMode::Auto : OpenMode::View);
    }

    element.setAllowEdit(sourceAllowEdit);
    element.setAllowEditCarrierCode();

    return isValid;
}

public boolean leaveRecord(boolean _forceUpdate = false)
{
    boolean ret;

    ret = super(_forceUpdate);

    // AllowCreate must be set to true to support InsertIfEmpty.
    transportationDocument_ds.allowCreate(true);

    return ret;
}

public void written()
{
    super();

    if (!transportationDocumentRecId)
    {
        ttsbegin;
        // If the master record did not have a TransportationDocument reference then set it now.
        sourceRecord.(transportationDocumentFieldId) = transportationDocument.RecId;
        sourceRecord.write();
        ttscommit;
    }
}

public void init()
{
    super();

    // Never allow the record to be deleted.
    transportationDocument_ds.allowDelete(false);
}

public void initValue()
{
    super();

    transportationDocument.initFromCommon(sourceRecord);
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoMX]))
    {
        if (sourceRecord.TableId == tablenum(InventTransferTable) &&
            transportationDocument.CarrierType == CarrierType_W::InvoiceAccount)
        {
            ret = checkFailed(strfmt("@GLS112813",
                                     transportationDocument.CarrierType,
                                     fieldpname(TransportationDocument, CarrierType)));
        }
    }

    return ret;
}

public void modified()
{
    super();

    element.setAllowEditCarrierCode();
    transportationDocument_ds.refresh();
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common                          ret;

    FieldBinding fieldBinding = AbsoluteFieldBinding::construct(fieldStr(LogisticsLocation, Description), tableStr(LogisticsLocation));
    var loadedAddressName = _formReferenceControl.filterValue(fieldBinding).value();

    if (loadedAddressName)
    {
        ret = element.getPostalAddress(loadedAddressName);
    }

    return ret;
}

