public void clicked()
{
    MenuFunction    menuFunction;
    Args            args;

    menuFunction = new MenuFunction(transportationDocument.getLoadedPostalAddressNewMenuItemName(), MenuItemType::Display);
    menuFunction.enumTypeParameter(enumNum(LogisticsLocationRoleType));
    menuFunction.enumParameter(enum2int(LogisticsLocationRoleType::Lading_W));

    args = new Args(element);
    args.record(transportationDocument);

    menuFunction.run(args);
}

