[Form]
public class TrvCashAdvances extends FormRun implements TrvIWorkflowEnabledForm
{
    NumberSeqFormHandler                numberSeqFormHandler;

    #Task

}

/// <summary>
/// Initialize the default values to dimension controls and set the form design mode based on the calling menu item.
/// </summary>
public void init()
{
    if (!element.args() || !element.args().menuItemName())
    {
        throw error("@SYS22996");
    }

    super();

    if (HcmWorkerLookup::currentWorker() == 0)
    {
        // The form must close because the user is not a worker.
        this.lifecycleHelper().cancelFormInitialization(Exception::Error, TrvListPageHelper::getCurrentUserNotWorkerErrorMessage(TrvCashAdvance_DS.table()));
    }
    else
    {
        if (element.args().menuItemName() == menuitemdisplaystr(TrvCashAdvances))
        {
            // Adds the current worker range and throws an error if user is not mapped to an employee
            TrvListPageHelper::addWorkerRange(TrvCashAdvance_ds.query());
        }

        element.updateControls();
    }
}

/// <summary>
/// Creates and returns an instance of the number sequence form handler.
/// </summary>
/// <returns>
/// An instance of the number sequence handler for the form.
/// </returns>
public NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(TrvParametersTableHelper::numRefCashAdvanceNumber().NumberSequenceId,
                                                             element,
                                                             TrvCashAdvance_ds,
                                                             fieldNum(TrvCashAdvance, CashAdvanceNumber));
    }
    return numberSeqFormHandler;
}

/// <summary>
/// Workflow event handler. Updates the UI to reflect the status after workflow action is taken.
/// Return back to the grid view.
/// </summary>
void workflowActionDone(TrvWorkflowAction actionType)
{
    TrvCashAdvance_ds.research();
    TrvCashAdvance_ds.active();
    element.task(#taskSwitchToGridView);
}

public void workflowActionFailed(TrvWorkflowAction actionType)
{
    // Any action that needs to be done if the workflow action failed, can be done here.
}

/// <summary>
/// Checks whether the form was opened as a submitter using the menu item or if the form should be in AP clerk mode.
/// </summary>
/// <returns>True if the form is opened not as submitter, else False.</returns>
private boolean isFormOpenedInAPClerkMode()
{
    if (element.args() && element.args().menuItemName() == menuitemdisplaystr(TrvCashAdvances))
    {
        return false;
    }

    return true;
}

/// <summary>
/// Set the form design mode and form controls' enable and visible properties according to the menu item
/// that opened the List page and worker employment.
/// </summary>
private void updateControls()
{
    // Hide "Pay and Transfer". It is visible in the active method if the cash advance is in Approved status.
    PayAndTransfer.visible(false);

    DimensionEntryControl.parmCompany(CompanyInfo::find().DataArea);
    DimensionEntryControl.reactivate();

    switch (element.args().menuItemName())
    {
        case  menuitemdisplaystr(TrvCashAdvances):
            General_RequestingWorker.visible(false);

            // Set the form design mode to Auto if the form is open from my cash advances menu item
            // and if the worker can create new cash advances if employed in the current company.
            // Otherwise set to view and disallow create.
            boolean canCreate = TrvAccessHelper::canCreateNewTransactions();
            element.design().viewEditMode(canCreate ? ViewEditMode::Auto : ViewEditMode::View);
            TrvCashAdvance_ds.allowCreate(canCreate);

            // Update workflow controls
            element.design().workflowEnabled(canCreate);
            element.updateWorkflowControls();

            // Set the cards view for employee.
            element.setCashAdvanceView();
            break;

        case menuitemdisplaystr(TrvCashAdvancesApprovedPaid):
        case menuitemdisplaystr(TrvCashAdvancesMyApprovals):
        case menuitemdisplaystr(TrvCashAdvancesConfirmed):
        case menuitemdisplaystr(TrvCashAdvancesWorkItem):
        case menuitemdisplaystr(TrvCashAdvancesStatusReset):
            // Set the form design mode to View if the form is open from All cash advances/Confirmed cash advances/Cash advances for my review menu item.
            element.design().viewEditMode(ViewEditMode::View);
            TrvCashAdvance_ds.allowCreate(false);
            TrvCashAdvance_ds.allowDelete(false);

            // Set the tabular view for AP Clerk.
            element.setCashAdvanceView(false);
            break;

        default:
            throw error("@SYS22996");
    }
}

/// <summary>
/// Shows and hides the payment information, account type and amount balance fields.
/// </summary>
private void showFields()
{
    // Show "Pay and Transfer" if the cash advance is in Approved status.
    PayAndTransfer.visible(TrvCashAdvance.CashAdvanceStatus == TrvWorkflowCashAdvanceStatus::Approved && element.isFormOpenedInAPClerkMode());

    // Show "Payment information" group, Amount Balance, Account type if the cash advance is in Paid status.
    boolean showField = TrvCashAdvance.CashAdvanceStatus == TrvWorkflowCashAdvanceStatus::Paid;
    PaidInfo.visible(showField);
    Amount_Balance.visible(showField);
    TrvCashAdvance_AccountType.visible(showField);

    Amount_M_editExchRate.visible(TrvCashAdvance.isTransactionAndCompanyCurrencyDifferent());
}

/// <summary>
/// Sets the view to show either a cards view for employee or tabular view for AP Clerk view.
/// Hides the show menu button to switch views in the AP Clerk view.
/// </summary>
/// <param name = "_isSubmitter">
/// A boolean variable to indicate if the current view is submitter's or approver's. Defaults to employee's view.
/// </param>
private void setCashAdvanceView(boolean _isSubmitter = true)
{
    EmployeeListQuickFilterControl.visible(_isSubmitter);
    EmployeeCashAdvanceList.visible(_isSubmitter);
    ApproverCashAdvanceList.visible(!_isSubmitter);
    ApproverListQuickFilterControl.visible(!_isSubmitter);
    CashAdvanceGroupBy.visible(_isSubmitter);
}

/// <summary>
/// Override the "edit" button to allow edit only in Details view
/// and whenever in grid view, toggle back to view mode.
/// </summary>
/// <param name = "_taskId"></param>
/// <returns></returns>
public int task(int _taskId)
{
    int ret;

    switch (_taskId)
    {
        case #taskEditRecord:
            // Editing should only happen in Details view.
            element.task(#taskSwitchToDetailsView);
            break;

        case #taskSwitchToGridView:
            // Only if it's in edit mode, save and complete editing record.
            if (!element.inViewMode())
            {
                element.task(#taskSave);
                element.task(#taskDoneEditRecord);
            }
            break;

        default:
            break;
    }

    ret = super(_taskId);
    return ret;
}

public int active()
{
    int ret;

    ret = super();

    TrvCashAdvance_ds.allowEdit(TrvCashAdvance.isEditable());
    TrvCashAdvance_ds.allowDelete(TrvCashAdvance.canDelete());
    element.showFields();

    return ret;
}

