/// <summary>
/// On initializing the Query will add the correct sorting according to the caller of the form.
/// </summary>
public void init()
{
    super();

    if (!element.isFormOpenedInAPClerkMode())
    {
        this.queryBuildDataSource().sortClear();
        this.queryBuildDataSource().addSortField(fieldNum(TrvCashAdvance, CashAdvanceStatus), SortOrder::Ascending);
        this.queryBuildDataSource().addSortField(fieldNum(TrvCashAdvance, RequestedDate), SortOrder::Ascending);
    }

    switch (element.args().menuItemName())
    {
        case menuitemdisplaystr(TrvCashAdvancesApprovedPaid):
        case menuitemdisplaystr(TrvCashAdvancesMyApprovals):
        case menuitemdisplaystr(TrvCashAdvancesConfirmed):
        case menuitemdisplaystr(TrvCashAdvancesWorkItem):
            element.design().showNewButton(AutoNoYes::No);
            element.design().showDeleteButton(AutoNoYes::No);

            // When the form opens from the TrvCashAdvancesWorkItem menu item, filter to the given record.
            if (element.args().menuItemName() == menuItemDisplayStr(TrvCashAdvancesWorkItem) && element.args().record())
            {
                QueryBuildRange range = this.queryBuildDataSource().addRange(fieldNum(TrvCashAdvance, RecId));
                range.value(queryValue(element.args().record().RecId));
                range.status(RangeStatus::Hidden);
            }
            break;
    }
}

