public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(TrvDestinations), this);

    sysTableLookup.addLookupField(fieldNum(TrvDestinations, Destination),true);
    sysTableLookup.addLookupField(fieldNum(TrvDestinations, Description));
    queryBuildDataSource = query.addDataSource(tableNum(TrvDestinations));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

